module.exports = {
  '*.(js|ts)': ['eslint --fix', 'prettier --write'],
  'appium-config-schema.js': () => [
    'npm run --workspace=./packages/schema build',
    'git add -A packages/schema/lib/appium-config.schema.json',
    'npm run --workspace=./packages/types build',
    'git add -A packages/types/lib/appium-config.ts',
  ],
  '!(package|package-lock)*.json': ['prettier --write'],
};

// @ts-check

'use strict';

module.exports = {
  require: ['ts-node/register'],
  // forbids use of .only() in CI
  forbidOnly: Boolean(process.env.CI),
  color: true,
  timeout: '5s',
};

{
  "$schema": "https://docs.renovatebot.com/renovate-schema.json",
  "extends": ["github>appium/appium//renovate/default"],
  "schedule": [
    "after 10pm",
    "before 5:00am"
  ],
  "timezone": "America/Vancouver"
}

import tsConfig from '@appium/eslint-config-appium-ts';

export default [
  ...tsConfig,
  {
    ...tsConfig.find(({name}) => name === 'Test Files'),
    name: 'Test Support',
    files: [
      'packages/test-support/lib/**',
      'packages/driver-test-support/lib/**',
      'packages/plugin-test-support/lib/**',
    ],
  },
  {
    name: 'Ignores',
    ignores: [
      '**/build-fixtures/**',
      'packages/appium/docs/**/assets/**',
      'packages/appium/docs/**/js/**',
      'packages/appium/sample-code/**',
    ],
  },
];

{
  "$schema": "https://json.schemastore.org/lerna",
  "changelogPreset": "conventional-changelog-conventionalcommits",
  "command": {
    "add": {
      "exact": true
    },
    "publish": {
      "message": "chore: publish"
    },
    "run": {
      "concurrency": 8
    },
    "version": {
      "conventionalCommits": true,
      "createRelease": "github",
      "allowBranch": "master"
    }
  },
  "ignoreChanges": ["**/test/**", "**/*.md"],
  "useNx": false,
  "version": "independent"
}

