module.exports = {
  extends: ['eslint:recommended', 'plugin:compat/recommended', 'prettier'],
  globals: {
    self: false,
  },
  env: {
    node: true,
    es2020: true,
  },
  parserOptions: {
    sourceType: 'module',
  },
  rules: {
    'no-console': 'warn',

    // temporarily disabled until the violating places are fixed.
    'no-func-assign': 'off',
    'no-sparse-arrays': 'off',

    // Best Practices //
    //----------------//
    'default-case': 'warn',
    'guard-for-in': 'warn',
    'no-alert': 'error',
    'no-caller': 'error',
    'no-div-regex': 'warn',
    'no-eval': 'error',
    'no-extend-native': 'error',
    'no-extra-bind': 'error',
    'no-floating-decimal': 'error',
    'no-implied-eval': 'error',
    'no-iterator': 'error',
    'no-labels': 'error',
    'no-lone-blocks': 'error',
    'no-loop-func': 'error',
    'no-multi-str': 'warn',
    'no-global-assign': 'error',
    'no-new': 'error',
    'no-new-func': 'error',
    'no-new-wrappers': 'error',
    'no-octal-escape': 'error',
    'no-process-env': 'error',
    'no-proto': 'error',
    'no-return-assign': 'error',
    'no-script-url': 'error',
    'no-self-compare': 'error',
    'no-sequences': 'error',
    'no-throw-literal': 'error',
    'no-unused-expressions': 'error',
    'no-warning-comments': 'warn',
    'no-with': 'error',
    radix: 'error',

    // Variables //
    //-----------//
    'no-label-var': 'error',
    'no-undef-init': 'error',
    'no-use-before-define': ['error', 'nofunc'],

    // ECMAScript 6 //
    //--------------//
    'no-var': 'error',
  },
};

/* eslint-disable no-process-env */
module.exports = function (grunt) {
  grunt.initConfig({
    pkg: grunt.file.readJSON('package.json'),

    clean: ['tmp', 'dist', 'tests/integration/**/node_modules'],

    copy: {
      dist: {
        options: {
          processContent: function (content) {
            return (
              grunt.template.process(
                '/**!\n\n @license magnet:?xt=urn:btih:d3d9a9a6595521f9666a5e94cc830dab83b65699&dn=expat.txt Expat\n <%= pkg.name %> v<%= pkg.version %>\n\n<%= grunt.file.read("LICENSE") %>\n*/\n'
              ) +
              content +
              '\n// @license-end\n'
            );
          },
        },
        files: [{ expand: true, cwd: 'dist/', src: ['*.js'], dest: 'dist/' }],
      },
      components: {
        files: [
          {
            expand: true,
            cwd: 'components/',
            src: ['**'],
            dest: 'dist/components',
          },
          {
            expand: true,
            cwd: 'dist/',
            src: ['*.js'],
            dest: 'dist/components',
          },
        ],
      },
    },

    babel: {
      options: {
        sourceMaps: 'inline',
        loose: ['es6.modules'],
        auxiliaryCommentBefore: 'istanbul ignore next',
      },
      cjs: {
        files: [
          {
            cwd: 'lib/',
            expand: true,
            src: '**/!(index).js',
            dest: 'dist/cjs/',
          },
        ],
      },
    },
    webpack: {
      options: {
        context: __dirname,
        output: {
          path: 'dist/',
          library: 'Handlebars',
          libraryTarget: 'umd',
        },
      },
      handlebars: {
        entry: './dist/cjs/handlebars.js',
        output: {
          filename: 'handlebars.js',
        },
      },
      runtime: {
        entry: './dist/cjs/handlebars.runtime.js',
        output: {
          filename: 'handlebars.runtime.js',
        },
      },
    },

    uglify: {
      options: {
        mangle: true,
        compress: true,
        preserveComments: /(?:^!|@(?:license|preserve|cc_on))/,
      },
      dist: {
        files: [
          {
            cwd: 'dist/',
            expand: true,
            src: ['handlebars*.js', '!*.min.js'],
            dest: 'dist/',
            rename: function (dest, src) {
              return dest + src.replace(/\.js$/, '.min.js');
            },
          },
        ],
      },
    },

    concat: {
      tests: {
        src: ['spec/!(require).js'],
        dest: 'tmp/tests.js',
      },
    },

    connect: {
      server: {
        options: {
          base: '.',
          hostname: '*',
          port: 9999,
        },
      },
    },

    shell: {
      integrationTests: {
        command: './tests/integration/run-integration-tests.sh',
      },
    },

    watch: {
      scripts: {
        options: {
          atBegin: true,
        },

        files: ['src/*', 'lib/**/*.js', 'spec/**/*.js'],
        tasks: ['on-file-change'],
      },
    },
  });

  // Load tasks from npm
  grunt.loadNpmTasks('grunt-contrib-clean');
  grunt.loadNpmTasks('grunt-contrib-concat');
  grunt.loadNpmTasks('grunt-contrib-connect');
  grunt.loadNpmTasks('grunt-contrib-copy');
  grunt.loadNpmTasks('grunt-contrib-uglify');
  grunt.loadNpmTasks('grunt-contrib-watch');
  grunt.loadNpmTasks('grunt-babel');
  grunt.loadNpmTasks('grunt-shell');
  grunt.loadNpmTasks('grunt-webpack');

  grunt.task.loadTasks('tasks');

  grunt.registerTask('node', ['babel:cjs']);
  grunt.registerTask('globals', ['webpack']);
  grunt.registerTask('release', 'Build final packages', [
    'uglify',
    'test:min',
    'copy:dist',
    'copy:components',
  ]);

  grunt.registerTask('on-file-change', ['build', 'concat:tests', 'test']);

  // === Primary tasks ===
  grunt.registerTask('dev', ['clean', 'connect', 'watch']);
  grunt.registerTask('default', ['clean', 'build', 'test', 'release']);
  grunt.registerTask('test', ['test:bin', 'test:cov']);
  grunt.registerTask('bench', ['metrics']);
  grunt.registerTask('prepare', ['build', 'concat:tests']);
  grunt.registerTask(
    'build',
    'Builds a distributable version of the current project',
    ['node', 'globals']
  );
  grunt.registerTask('integration-tests', [
    'default',
    'shell:integrationTests',
  ]);
};

module.exports = {
  'check-coverage': true,
  branches: 100,
  lines: 100,
  functions: 100,
  statements: 100,
  exclude: ['**/spec/**'],
  reporter: 'html',
};

