import cypress from 'cypress'

export default cypress.defineConfig({
  e2e: {
    supportFile: 'cypress/support/index.js',
    video: false,
    experimentalRunAllSpecs: true,
    experimentalWebKitSupport: true,
  },
})

import jsdoc from 'eslint-plugin-jsdoc'
import sweetAlert2EslintConfig from '@sweetalert2/eslint-config'
// TODO: https://github.com/mozilla/eslint-plugin-no-unsanitized/issues/241
// import eslintPluginNoUnsanitized from 'eslint-plugin-no-unsanitized'
import pluginCypress from 'eslint-plugin-cypress/flat'
import globals from 'globals'

export default [
  ...sweetAlert2EslintConfig,
  // eslintPluginNoUnsanitized,
  jsdoc.configs['flat/recommended'],
  pluginCypress.configs.recommended,
  {
    plugins: {
      jsdoc,
    },
    rules: {
      '@typescript-eslint/ban-ts-comment': 0,
      '@typescript-eslint/no-empty-interface': 0,
      '@typescript-eslint/no-explicit-any': 0,
      '@typescript-eslint/no-this-alias': 0,
      '@typescript-eslint/no-unused-vars': ['error', { varsIgnorePattern: '_' }],
      '@typescript-eslint/triple-slash-reference': 0,
      'jsdoc/require-param-description': 0,
      'jsdoc/require-property-description': 0,
      'jsdoc/require-returns-description': 0,
      'jsdoc/no-undefined-types': 0,
      'jsdoc/tag-lines': 0,
      'jsdoc/valid-types': 0,
    },
  },
  {
    files: ['sweetalert2.d.ts'],
    rules: {
      'jsdoc/require-param': 0,
      'jsdoc/require-returns': 0,
      'jsdoc/require-param-type': 0,
    },
  },
  {
    files: ['cypress/**/*.js'],
    rules: {
      '@typescript-eslint/no-unused-expressions': 0,
      'no-unsanitized/property': 0,
      'no-unused-expressions': 0,
      'jsdoc/require-jsdoc': 0,
    },
  },
  {
    files: ['rollup.config.js', 'release.config.js'],
    languageOptions: {
      globals: {
        ...globals.node,
      },
    },
  },
  {
    files: ['tools/build*'],
    rules: {
      '@typescript-eslint/no-unused-expressions': 0,
    },
    languageOptions: {
      globals: {
        ...globals.node,
      },
    },
  },
]

module.exports = {
  debug: true,
  branches: ['main'],
  verifyConditions: ['@semantic-release/changelog', '@semantic-release/npm', '@semantic-release/github'],
  prepare: [
    {
      path: '@semantic-release/exec',
      cmd: 'VERSION=${nextRelease.version} ./node_modules/.bin/zx tools/build-dist.mjs',
    },
    '@semantic-release/changelog',
    '@semantic-release/npm',
    '@semantic-release/git',
  ],
  publish: [
    '@semantic-release/npm',
    [
      '@semantic-release/github',
      {
        assets: [
          { path: 'dist/sweetalert2.all.js' },
          { path: 'dist/sweetalert2.all.min.js' },
          { path: 'dist/sweetalert2.css' },
          { path: 'dist/sweetalert2.esm.all.js' },
          { path: 'dist/sweetalert2.esm.all.min.js' },
          { path: 'dist/sweetalert2.esm.js' },
          { path: 'dist/sweetalert2.esm.min.js' },
          { path: 'dist/sweetalert2.js' },
          { path: 'dist/sweetalert2.min.css' },
          { path: 'dist/sweetalert2.min.js' },
        ],
      },
    ],
  ],
  success: [
    '@semantic-release/github',
    {
      path: '@semantic-release/exec',
      cmd: './node_modules/.bin/zx tools/purge-jsdelivr.mjs',
    },
  ],
}

import { readFileSync } from 'fs'
import babel from '@rollup/plugin-babel'
import terser from '@rollup/plugin-terser'

const packageJson = JSON.parse(readFileSync('package.json'))
const version = process.env.VERSION || packageJson.version

const banner = `/*!
* ${packageJson.name} v${version}
* Released under the ${packageJson.license} License.
*/`

const footer = `\
if (typeof this !== 'undefined' && this.Sweetalert2){\
this.swal = this.sweetAlert = this.Swal = this.SweetAlert = this.Sweetalert2\
}`

const output = {
  format: 'umd',
  name: 'Sweetalert2',
  file: 'dist/sweetalert2.js',
  banner,
  footer,
}
const outputEsm = {
  format: 'es',
  file: 'dist/sweetalert2.esm.js',
  banner,
}

export default {
  plugins: [
    babel({
      babelHelpers: 'bundled',
      presets: [['@babel/preset-env', { include: ['transform-optional-chaining', 'transform-class-properties'] }]],
      targets: 'defaults',
    }),
  ],
  input: 'src/sweetalert2.js',
  output: [
    output,
    outputEsm,
    {
      ...output,
      file: 'dist/sweetalert2.min.js',
      plugins: [terser()],
    },
    {
      ...outputEsm,
      file: 'dist/sweetalert2.esm.min.js',
      plugins: [terser()],
    },
  ],
  // https://github.com/rollup/rollup/issues/2271
  onwarn(warning, rollupWarn) {
    if (warning.code !== 'CIRCULAR_DEPENDENCY' && warning.code !== 'THIS_IS_UNDEFINED') {
      rollupWarn(warning)
    }
  },
}
