{
  "extends": ["semistandard", "standard-jsx"],
  "parserOptions": {
    "ecmaVersion": 12
  },
  "rules": {
    /* These rules are incompatible with ES5. */
    "no-var": "off",
    "object-shorthand": "off",
    "prefer-const": "off",
    "prefer-regex-literals": "off",

    /* These rules are compatible with ES5
       However they involve non-trivial code changes
       Therefore need more careful review before adopting. */
    "array-callback-return": "off",
    "no-mixed-operators": "off",
    "no-unreachable-loop": "off",
    "no-useless-return": "off",
    "prefer-promise-reject-errors": "off",

    /* These rules are compatible with ES5
       However they involve large-scale changes to the codebase, so
       careful co-ordination is needed in adopting the rule to avoid
       creating merge issues for other PRs. */
    "dot-notation": "off",
    "indent": "off",
    "no-multi-spaces": "off",
    "no-unused-vars": "off",
    "object-curly-spacing": "off",
    "quote-props": "off"
  },
  "overrides": [
    {
      /* Code within /src is restricted to using ES5 JavaScript
        The exception is that ES6 classes are used sparingly - see exceptions below. */
      "files": ["./src/**/*.js"],
      "parserOptions": {
        "sourceType": "script",
        "ecmaVersion": 5
      }
    },
    {
      /* These modules use ES6 classes, and so are parsed as ES6 to avoid errors. */
      "files": ["./src/core/**/a-*.js"],
      "parserOptions": {
        "ecmaVersion": 6
      }
    },
    {
      /* This module use ES6 classes, and so is parsed as ES6 to avoid errors. */
      "files": ["./src/extras/primitives/primitives.js"],
      "parserOptions": {
        "ecmaVersion": 6
      }
    },
    {
      /* This module uses ES6 */
      "files": ["./src/components/scene/real-world-meshing.js"],
      "parserOptions": {
        "ecmaVersion": 6
      }
    },
    {
      /* This module uses ES8 async / await due to WebXR Anchor Module integration */
      "files": ["./src/components/anchored.js"],
      "parserOptions": {
        "ecmaVersion": 8
      }
    },
    {
      /* This module uses ES6 for‚Ä¶of loops, and so is parsed as ES6 to avoid errors. */
      "files": ["./src/components/hand-tracking-controls.js"],
      "parserOptions": {
        "ecmaVersion": 6
      }
    },
    {
      /* This code is external, and the ES5 restrictions do not apply to it. */
      "files": ["./src/lib/**/*.js"],
      "parserOptions": {
        "sourceType": "module",
        "ecmaVersion": 12
      }
    }
  ]
}

var path = require('path');
var webpack = require('webpack');

module.exports = {
  entry: './src/index.js',
  output: {
    library: 'AFRAME',
    libraryTarget: 'umd',
    path: path.resolve(__dirname, 'dist'),
    publicPath: '/dist/',
    filename: 'aframe-master.js'
  },
  devtool: 'source-map',
  mode: 'development',
  devServer: {
    port: process.env.PORT || 9000,
    hot: false,
    liveReload: true,
    static: {
      directory: 'examples'
    }
  },
  plugins: [
    new webpack.DefinePlugin({
      INSPECTOR_VERSION: JSON.stringify(
        process.env.INSPECTOR_VERSION
      )
    }),
    new webpack.ProvidePlugin({
      Buffer: ['buffer', 'Buffer']
    })
  ],
  module: {
    rules: [
      {
        test: /\.js$/,
        use: {
          loader: 'babel-loader'
        }
      },
      {
        test: /\.css$/,
        use: ['style-loader', 'css-loader']
      }
    ]
  }
};

var path = require('path');
var TerserPlugin = require('terser-webpack-plugin');

module.exports = {
  extends: ['webpack.config.js'],
  output: {
    library: 'AFRAME',
    libraryTarget: 'umd',
    path: path.resolve(__dirname, 'dist'),
    publicPath: '/dist/',
    filename: 'aframe-master.min.js'
  },
  mode: 'production',
  optimization: {
    minimize: true,
    minimizer: [
      new TerserPlugin({
        terserOptions: {
          compress: {
            passes: 2
          },
          format: {
            comments: false
          }
        },
        extractComments: false
      })
    ]
  }
};
