{
    "gitbook": "3.2.2",
    "title": "Mailspring Plugin SDK",
    "root": "./app/build/docs_src/",
    "plugins": [
        "search",
        "lunr",
        "-fontsettings",
        "-sharing",
        "anchors",
        "github",
        "editlink",
        "favicon"
    ],
    "pluginsConfig": {
        "editlink": {
          "base": "https://github.com/Foundry376/Mailspring/tree/master",
          "label": "Edit This Page",
          "multilingual": false
        },
        "github": {
            "url": "https://github.com/Foundry376/Mailspring"
        },
        "favicon": {
            "shortcut": "/img/favicon.png"
        }
    },

  "links": {
    "sharing": {
      "facebook": false,
      "twitter": false
    }
  }

}
