module.exports = {
  extends: [
    'standard',
    'plugin:vue/recommended',
    'plugin:prettier/recommended',
    'plugin:markdown/recommended'
  ],
  plugins: ['jest', 'node', 'promise'],
  parserOptions: {
    parser: 'babel-eslint',
    sourceType: 'module'
  },
  env: {
    browser: true,
    es6: true,
    'jest/globals': true
  },
  rules: {
    'no-unused-vars': [
      'error',
      {
        vars: 'all',
        args: 'after-used',
        ignoreRestSiblings: false
      }
    ],
    'object-shorthand': ['error', 'properties'],
    'spaced-comment': 'off', // needed to ignore `/*#__PURE__*/` comments
    'vue/custom-event-name-casing': 'off',
    'vue/html-self-closing': [
      'error',
      {
        html: {
          void: 'never',
          normal: 'never',
          component: 'never'
        }
      }
    ],
    'vue/max-attributes-per-line': ['error', { singleline: 4 }],
    'vue/no-v-html': 'off',
    'vue/one-component-per-file': 'off',
    'vue/require-default-prop': 'off',
    'vue/require-prop-types': 'off',
    'vue/singleline-html-element-content-newline': 'off'
  }
}

{
  "printWidth": 100,
  "proseWrap": "always",
  "semi": false,
  "singleQuote": true,
  "tabWidth": 2,
  "overrides": [
    {
      "files": ".github/**/*.md",
      "options": {
        "proseWrap": "preserve"
      }
    },
    {
      "files": "*.scss",
      "options": {
        "singleQuote": false
      }
    }
  ]
}

{
  "scripts": {
    "postchangelog": "./node_modules/.bin/prettier --write CHANGELOG.md"
  },
  "skip": {
    "commit": true,
    "tag": true
  },
  "types": [
    {
      "type": "fix",
      "section": "Bug Fixes"
    },
    {
      "type": "feat",
      "section": "Features"
    },
    {
      "type": "perf",
      "section": "Performance"
    },
    {
      "type": "docs",
      "hidden": true
    },
    {
      "type": "style",
      "hidden": true
    },
    {
      "type": "refactor",
      "hidden": true
    },
    {
      "type": "chore",
      "hidden": true
    },
    {
      "type": "test",
      "hidden": true
    }
  ]
}

module.exports = api => {
  const isDocs = api.env('docs')

  const presets = []
  if (!isDocs) {
    presets.push(['@babel/env', { useBuiltIns: 'entry', corejs: { version: 3 } }])
  }

  return {
    presets,
    env: {
      es: {
        plugins: [['@babel/plugin-transform-modules-commonjs', { loose: true }]]
      },
      esm: {
        presets: [['@babel/env', { modules: false }]]
      },
      test: {
        presets: [['@babel/env', { targets: { node: 'current' } }]]
      }
    }
  }
}

const useVue3 = 'USE_VUE3' in process.env

const moduleNameMapper = useVue3
  ? {
      '^vue$': '@vue/compat',
      '^@vue/test-utils$': '@vue/test-utils-vue3'
    }
  : {}

module.exports = {
  testRegex: 'spec.js$',
  moduleFileExtensions: ['js', 'vue'],
  moduleNameMapper,
  transform: {
    '^.+\\.js$': 'babel-jest'
  },
  transformIgnorePatterns: ['/node_modules(?![\\\\/]vue-test-utils-compat[\\\\/])'],
  coverageDirectory: useVue3 ? './coverage-vue3' : './coverage/',
  testEnvironmentOptions: {
    pretendToBeVisual: true
  },
  setupFilesAfterEnv: ['./tests/setup.js']
}

{
  "headers": [
    {
      "source": "/(.*)",
      "headers": [{ "key": "X-Robots-Tag", "value": "" }]
    }
  ]
