{
  "env": {
    "test": {
      "presets": [["airbnb", { looseClasses: true }]],
      "plugins": [
        ["import-path-replace", {
          "rules": [
            {
              "match": "../src/",
              "replacement": "../lib/"
            }
          ]
        }],
        ["inline-react-svg", {
          "svgo": false
        }],
        ["transform-replace-object-assign", { "moduleSpecifier": "object.assign" }],
        "./scripts/pure-component-fallback.js",
        "istanbul",
      ]
    },

    "development": {
      "presets": [["airbnb", { looseClasses: true }]],
      "plugins": [
        ["inline-react-svg", {
          "svgo": false
        }],
        ["transform-replace-object-assign", { "moduleSpecifier": "object.assign" }],
        "./scripts/pure-component-fallback.js",
      ],
    },

    "production": {
      "presets": [["airbnb", { looseClasses: true, removePropTypes: true }]],
      "plugins": [
        ["inline-react-svg", {
          "svgo": false
        }],
        ["transform-replace-object-assign", { "moduleSpecifier": "object.assign" }],
        "./scripts/pure-component-fallback.js",
      ],
    },

    "cjs": {
      "presets": [["airbnb", { looseClasses: true, removePropTypes: true }]],
      "plugins": [
        ["inline-react-svg", {
          "svgo": false
        }],
        ["transform-replace-object-assign", { "moduleSpecifier": "object.assign" }],
        "./scripts/pure-component-fallback.js",
      ],
    },

    "esm": {
      "presets": [["airbnb", { looseClasses: true, modules: false, removePropTypes: true }]],
      "plugins": [
        ["inline-react-svg", {
          "svgo": false
        }],
        ["transform-replace-object-assign", { "moduleSpecifier": "object.assign" }],
        "./scripts/pure-component-fallback.js",
      ],
    },
  },
}

{
  "root": true,

  "extends": [
    "airbnb",
    "plugin:react-with-styles/recommended",
  ],

  "plugins": [
    "react-with-styles",
  ],

  "env": {
    "browser": true,
    "node": true,
  },

  "ignorePatterns": [
    "lib/",
    ".storybook/",
    "test/_helpers/",
    "webpack.config.js",
  ],

  "parser": "@babel/eslint-parser",

  "rules": {
    "max-len": "off",

    "react/forbid-foreign-prop-types": 2, // For babel-plugin-transform-react-remove-prop-types

    "jsx-a11y/click-events-have-key-events": 1, // TODO: enable

    "react-with-styles/no-unused-styles": 2,

    "no-restricted-imports": 0, // TODO: enable with full RTL support

    "react/jsx-props-no-spreading": 0, // TODO: re-evaluate

    "react/no-deprecated": 1, // TODO: update to UNSAFE_componentWillReceiveProps
  },

  "overrides": [
    {
      "files": "test/**/*",
      "env": {
        "mocha": true,
      },
      "extends": "airbnb",
      "rules": {
        "react/jsx-props-no-spreading": 0,
        //"import/no-extraneous-dependencies": [2, {
          //"devDependencies": true
        //}],
        "indent": [2, 2, {
          "MemberExpression": "off",
        }],
        "react/function-component-definition": "off",
      },
    },
  ],

  "settings": {
    "propWrapperFunctions": ["forbidExtraProps", "exact", "Object.freeze"],
  },
}

/* eslint no-param-reassign:0, import/no-extraneous-dependencies:0 */
const path = require('path');
const webpack = require('webpack');

module.exports = (config) => {
  config.set({
    basePath: '',

    frameworks: ['mocha', 'sinon', 'chai'],

    files: ['test/browser-main.js'],

    webpack: {
      mode: 'development',
      externals: {
        sinon: true,
      },
      plugins: [
        // https://github.com/cheeriojs/cheerio/issues/836
        new webpack.NormalModuleReplacementPlugin(/^\.\/package$/, (result) => {
          if (/cheerio/.test(result.context)) {
            result.request = './package.json';
          }
        }),
      ],
      module: {
        rules: [
          {
            test: /\.jsx?$/,
            loader: 'babel-loader',
            include: [
              path.join(__dirname, 'src'),
              path.join(__dirname, 'test'),
              require.resolve('airbnb-js-shims'),
            ],
            options: {
              presets: [
                // setting modules to false so it does not transform twice
                ['airbnb', { modules: false }],
                // transform to cjs so sinon can stub properly
                ['@babel/preset-env', { modules: 'cjs' }],
              ],
            },
          },

          // Inject the Airbnb shims into the bundle
          { test: /test\/_helpers/, loader: 'imports-loader?shims=airbnb-js-shims' },
        ],
      },
      resolve: {
        extensions: ['.js', '.jsx'],
      },
    },

    webpackMiddleware: {
      progress: false,
      stats: false,
      debug: false,
      quiet: true,
    },

    preprocessors: {
      'test/**/*': ['webpack'],
    },

    reporters: ['progress'],

    port: 9876,

    colors: true,

    logLevel: config.LOG_INFO,

    autoWatch: false,

    browsers: ['Firefox'],

    singleRun: true,

    concurrency: Infinity,
  });
};
