import importPlugin from 'eslint-plugin-import';
import stylelintConfig from 'eslint-config-stylelint';
import stylelintJestConfig from 'eslint-config-stylelint/jest';

export default [
	{
		ignores: ['lib/**/*.cjs', '.coverage/*'],
	},
	...stylelintConfig,
	...stylelintJestConfig,
	{
		plugins: {
			import: importPlugin,
		},
		languageOptions: {
			globals: {
				testRule: 'readonly',
				testRuleConfigs: 'readonly',
			},
		},
		rules: {
			'import/extensions': ['error', 'ignorePackages'],
			'jest/no-standalone-expect': ['error', { additionalTestBlockFunctions: ['testFn'] }],
		},
	},
];

import jestPreset from 'jest-preset-stylelint';

const { getTestRule, getTestRuleConfigs } = jestPreset;

const loadLint = () => import('./lib/index.mjs').then((m) => m.default.lint);

global.testRule = getTestRule({ loadLint });
global.testRuleConfigs = getTestRuleConfigs({ loadLint });

import { clearEmittedDeprecationWarnings } from './lib/utils/emitDeprecationWarning.mjs';

afterEach(() => {
	clearEmittedDeprecationWarnings();
});

