'use strict';

module.exports = {
  presets: [
    '@babel/preset-react',
    [
      '@babel/preset-env',
      {
        loose: true,
        bugfixes: true,
      },
    ],
  ],
};

'use strict';

module.exports = {
  env: {
    browser: true,
    es6: true,
    node: true,
  },
  parserOptions: {
    sourceType: 'script',
    ecmaVersion: 2017,
  },
  extends: [
    'google',
    'plugin:react/recommended',
  ],
  settings: {
    react: {
      version: 'detect',
    },
  },
  rules: {
    'max-len': [
      'warn',
      {
        // 130 on GitHub, 80 on npmjs.org for README.md code blocks
        code: 130,
      },
    ],
    'arrow-parens': [
      'error',
      'as-needed',
    ],
    'space-before-function-paren': [
      'error',
      {
        anonymous: 'always',
        named: 'never',
      },
    ],
    'no-negated-condition': 'warn',
    'prefer-destructuring': [
      'off',
      {
        object: true,
        array: false,
      },
    ],
    'prefer-template': 'error',
    'strict': 'error',
    'spaced-comment': [
      'error',
      'always',
      {
        exceptions: [
          '/',
        ],
      },
    ],
  },
  overrides: [
    {
      files: [
        'src/**',
      ],
      parserOptions: {
        sourceType: 'module',
      },
    },
    {
      files: [
        'site/**',
      ],
      env: {
        node: false,
      },
      parserOptions: {
        sourceType: 'script',
      },
      rules: {
        'require-jsdoc': 'off',
        'strict': 'error',
      },
    },
  ],
};

[
  {
    "path": "dist/quicklink.js",
    "limit": "2 kB",
    "gzip": true
  },
  {
    "path": "dist/quicklink.mjs",
    "limit": "2 kB",
    "gzip": true
  },
  {
    "path": "dist/quicklink.modern.mjs",
    "limit": "1.6 kB",
    "gzip": true
  },
  {
    "path": "dist/quicklink.umd.js",
    "limit": "2 kB",
    "gzip": true
  }
]

