const fs = require('fs');
const gulp = require('gulp');
const eslint = require('gulp-eslint');
const karma = require('karma');
const size = require('gulp-size');
const rimraf = require('rimraf');
const dotenv = require('dotenv');
const exec = require('child_process').exec;

const pkg = require('./package.json');
const karmaDefaults = require('./karma.defaults.js');

if (fs.existsSync('./.env')) dotenv.config();

gulp.task('lint', () => {
  return gulp
    .src('./src/**/*.js')
    .pipe(eslint())
    .pipe(eslint.format())
    .pipe(eslint.failAfterError());
});

gulp.task('size', () => {
  const mainPath = './' + pkg.main;
  const minifiedPath = mainPath.replace('.js', '.min.js');

  return gulp
    .src([mainPath, minifiedPath])
    .pipe(
      size({
        showFiles: true,
        showTotal: false,
      })
    )
    .pipe(
      size({
        showFiles: true,
        showTotal: false,
        title: 'gzipped',
        gzip: true,
      })
    );
});

gulp.task('clean', (cb) => {
  rimraf('./*.log', cb);
});

gulp.task('test-local', (done) => {
  // Setup browsers.
  const browsers = ['Chrome'];

  new karma.Server(
    {
      configFile: __dirname + '/karma.conf.js',
      action: 'run',
      browsers,
    },
    (exitCode) => {
      done(exitCode);
    }
  ).start();
});

gulp.task('test-local-min', (done) => {
  // Setup browsers.
  const browsers = ['Chrome'];

  // Replace main file with minified version.
  const mainPath = './' + pkg.main;
  const minifiedPath = mainPath.replace('.js', '.min.js');
  const files = karmaDefaults.files.map((path) => {
    if (path === mainPath) return minifiedPath;
    return path;
  });

  new karma.Server(
    {
      configFile: __dirname + '/karma.conf.js',
      action: 'run',
      browsers,
      files,
    },
    (exitCode) => {
      done(exitCode);
    }
  ).start();
});

gulp.task('test-sauce', (done) => {
  // Setup browsers.
  const browsers = ['slChrome', 'slFirefox', 'slSafari'];

  new karma.Server(
    {
      configFile: __dirname + '/karma.conf.js',
      action: 'run',
      browsers,
    },
    (exitCode) => {
      done(exitCode);
    }
  ).start();
});

gulp.task('test-sauce-min', (done) => {
  // Setup browsers.
  const browsers = ['slChrome', 'slFirefox', 'slSafari'];

  // Replace main file with minified version.
  const mainPath = './' + pkg.main;
  const minifiedPath = mainPath.replace('.js', '.min.js');
  const files = karmaDefaults.files.map((path) => {
    if (path === mainPath) return minifiedPath;
    return path;
  });

  new karma.Server(
    {
      configFile: __dirname + '/karma.conf.js',
      action: 'run',
      browsers,
      files,
    },
    (exitCode) => {
      done(exitCode);
    }
  ).start();
});

gulp.task('validate-formatting', (cb) => {
  exec('npm run validate-formatting', (err, stdout, stderr) => {
    console.log(stdout);
    console.log(stderr);
    cb(err);
  });
});

gulp.task('bundle', (cb) => {
  exec('npm run bundle', (err, stdout, stderr) => {
    console.log(stdout);
    console.log(stderr);
    cb(err);
  });
});

gulp.task('minify', (cb) => {
  exec('npm run minify', (err, stdout, stderr) => {
    console.log(stdout);
    console.log(stderr);
    cb(err);
  });
});

gulp.task(
  'build',
  gulp.series('bundle', 'minify', (done) => {
    done();
  })
);

gulp.task(
  'pre-commit',
  gulp.series('lint', 'validate-formatting', (done) => {
    done();
  })
);

gulp.task(
  'test',
  gulp.series('lint', 'validate-formatting', 'test-sauce', 'test-sauce-min', 'clean', (done) => {
    done();
  })
);

const defaultSettings = require('./karma.defaults.js');

module.exports = function (config) {
  config.set(defaultSettings);
};

const pkg = require('./package.json');

module.exports = {
  basePath: '',
  frameworks: ['qunit'],
  plugins: ['karma-qunit', 'karma-chrome-launcher', 'karma-sauce-launcher', 'karma-story-reporter'],
  files: [
    './node_modules/web-animations-js/web-animations.min.js',
    './node_modules/prosthetic-hand/dist/prosthetic-hand.js',
    './node_modules/mezr/mezr.js',
    './' + pkg.main,
    './tests/index.js',
    './tests/utils.js',
    './tests/grid-constructor/*.js',
    './tests/grid-options/*.js',
    './tests/grid-methods/*.js',
    './tests/grid-events/*.js',
    './tests/item-methods/*.js',
  ],
  reporters: ['story', 'saucelabs'],
  colors: true,
  autoWatch: false,
  browserDisconnectTolerance: 2,
  singleRun: true,
  hostname: '127.0.0.1',
  sauceLabs: {
    testName: pkg.name + ' - ' + pkg.version + ' - unit tests',
  },
  customLaunchers: {
    slChrome: {
      base: 'SauceLabs',
      browserName: 'chrome',
      platform: 'Windows 10',
      version: 'latest',
    },
    slFirefox: {
      base: 'SauceLabs',
      browserName: 'firefox',
      platform: 'Windows 10',
      version: 'latest',
    },
    slSafari: {
      base: 'SauceLabs',
      browserName: 'safari',
      platform: 'macOS 10.12',
      version: 'latest',
    },
    slEdge: {
      base: 'SauceLabs',
      browserName: 'MicrosoftEdge',
      platform: 'Windows 10',
      version: 'latest',
    },
  },
};

