import globals from "globals";
import babelParser from "@babel/eslint-parser";
import path from "node:path";
import { fileURLToPath } from "node:url";
import js from "@eslint/js";
import { FlatCompat } from "@eslint/eslintrc";

const __filename = fileURLToPath(import.meta.url);
const __dirname = path.dirname(__filename);
const compat = new FlatCompat({
    baseDirectory: __dirname,
    recommendedConfig: js.configs.recommended,
    allConfig: js.configs.all
});

export default [...compat.extends("eslint:recommended"), {
    languageOptions: {
        globals: {
            ...globals.browser,
            ...globals.commonjs,
            '__dirname': 'readonly',
        },

        parser: babelParser,
        ecmaVersion: 6,
        sourceType: "module",

        parserOptions: {
            requireConfigFile: false,
        },
    },

    rules: {
        "no-sequences": "error",
    },
}];

// feel free to comment out anything to rollup
// a smaller chroma.js bundle
import chroma from './src/chroma.js';

// io --> convert colors
import './src/io/named/index.js';

// operators --> modify existing Colors
import './src/ops/alpha.js';
import './src/ops/clipped.js';
import './src/ops/darken.js';
import './src/ops/get.js';
import './src/ops/luminance.js';
import './src/ops/mix.js';
import './src/ops/premultiply.js';
import './src/ops/saturate.js';
import './src/ops/set.js';
import './src/ops/shade.js';

// interpolators
import './src/interpolator/rgb.js';
import './src/interpolator/lrgb.js';
import './src/interpolator/lab.js';
import './src/interpolator/lch.js';
import './src/interpolator/num.js';
import './src/interpolator/hcg.js';
import './src/interpolator/hsi.js';
import './src/interpolator/hsl.js';
import './src/interpolator/hsv.js';
import './src/interpolator/oklab.js';
import './src/interpolator/oklch.js';

// generators -- > create new colors
import average from './src/generator/average.js';
import bezier from './src/generator/bezier.js';
import blend from './src/generator/blend.js';
import cubehelix from './src/generator/cubehelix.js';
import mix from './src/generator/mix.js';
import random from './src/generator/random.js';
import scale from './src/generator/scale.js';

// other utility methods
import { analyze } from './src/utils/analyze.js';
import contrast from './src/utils/contrast.js';
import contrastAPCA from './src/utils/contrastAPCA.js';
import deltaE from './src/utils/delta-e.js';
import distance from './src/utils/distance.js';
import { limits } from './src/utils/analyze.js';
import valid from './src/utils/valid.js';
import input from './src/io/input.js';

// scale
import scales from './src/utils/scales.js';

// colors
import colors from './src/colors/w3cx11.js';
import brewer from './src/colors/colorbrewer.js';
import Color from './src/Color.js';

Object.assign(chroma, {
    analyze,
    average,
    bezier,
    blend,
    brewer,
    Color,
    colors,
    contrast,
    contrastAPCA,
    cubehelix,
    deltaE,
    distance,
    input,
    interpolate: mix,
    limits,
    mix,
    random,
    scale,
    scales,
    valid
});

export default chroma;

export {
    analyze,
    average,
    bezier,
    blend,
    brewer,
    Color,
    colors,
    contrast,
    contrastAPCA,
    cubehelix,
    deltaE,
    distance,
    input,
    limits,
    mix,
    mix as interpolate,
    random,
    scale,
    scales,
    valid
};

export * from './src/io/cmyk/index.js';
export * from './src/io/css/index.js';
export * from './src/io/gl/index.js';
export * from './src/io/hcg/index.js';
export * from './src/io/hex/index.js';
export * from './src/io/hsi/index.js';
export * from './src/io/hsl/index.js';
export * from './src/io/hsv/index.js';
export * from './src/io/lab/index.js';
export * from './src/io/lch/index.js';
export * from './src/io/num/index.js';
export * from './src/io/rgb/index.js';
export * from './src/io/temp/index.js';
export * from './src/io/oklab/index.js';
export * from './src/io/oklch/index.js';

// feel free to comment out anything to rollup
// a smaller chroma.js bundle
import chroma from './src/chroma.js';

// io --> convert colors
import { cmyk } from './src/io/cmyk/index.js';
import { css } from './src/io/css/index.js';
import { gl } from './src/io/gl/index.js';
import { hcg } from './src/io/hcg/index.js';
import { hex } from './src/io/hex/index.js';
import { hsi } from './src/io/hsi/index.js';
import { hsl } from './src/io/hsl/index.js';
import { hsv } from './src/io/hsv/index.js';
import { lab, getLabWhitePoint, setLabWhitePoint } from './src/io/lab/index.js';
import { lch, hcl } from './src/io/lch/index.js';
import { num } from './src/io/num/index.js';
import { rgb } from './src/io/rgb/index.js';
import { temp, kelvin, temperature } from './src/io/temp/index.js';
import { oklab } from './src/io/oklab/index.js';
import { oklch } from './src/io/oklch/index.js';

import './src/io/named/index.js';

// operators --> modify existing Colors
import './src/ops/alpha.js';
import './src/ops/clipped.js';
import './src/ops/darken.js';
import './src/ops/get.js';
import './src/ops/luminance.js';
import './src/ops/mix.js';
import './src/ops/premultiply.js';
import './src/ops/saturate.js';
import './src/ops/set.js';
import './src/ops/shade.js';

// interpolators
import './src/interpolator/rgb.js';
import './src/interpolator/lrgb.js';
import './src/interpolator/lab.js';
import './src/interpolator/lch.js';
import './src/interpolator/num.js';
import './src/interpolator/hcg.js';
import './src/interpolator/hsi.js';
import './src/interpolator/hsl.js';
import './src/interpolator/hsv.js';
import './src/interpolator/oklab.js';
import './src/interpolator/oklch.js';

// generators -- > create new colors
import average from './src/generator/average.js';
import bezier from './src/generator/bezier.js';
import blend from './src/generator/blend.js';
import cubehelix from './src/generator/cubehelix.js';
import mix from './src/generator/mix.js';
import random from './src/generator/random.js';
import scale from './src/generator/scale.js';

// other utility methods
import { analyze } from './src/utils/analyze.js';
import contrast from './src/utils/contrast.js';
import contrastAPCA from './src/utils/contrastAPCA.js';
import deltaE from './src/utils/delta-e.js';
import distance from './src/utils/distance.js';
import { limits } from './src/utils/analyze.js';
import valid from './src/utils/valid.js';
import input from './src/io/input.js';

// scale
import scales from './src/utils/scales.js';

// colors
import colors from './src/colors/w3cx11.js';
import brewer from './src/colors/colorbrewer.js';
import Color from './src/Color.js';

Object.assign(chroma, {
    analyze,
    average,
    bezier,
    blend,
    brewer,
    Color,
    colors,
    contrast,
    contrastAPCA,
    cubehelix,
    deltaE,
    distance,
    input,
    interpolate: mix,
    limits,
    mix,
    random,
    scale,
    scales,
    valid,
    cmyk,
    css,
    gl,
    hcg,
    hex,
    hsi,
    hsl,
    hsv,
    lab,
    lch,
    hcl,
    num,
    rgb,
    temp,
    kelvin,
    temperature,
    oklab,
    oklch,
    getLabWhitePoint,
    setLabWhitePoint
});

export default chroma;

import chroma from './src/chroma.js';

// feel free to comment out anything to rollup
// a smaller chroma.js bundle

// io --> convert colors
import { css } from './src/io/css/index.js';
import { hex } from './src/io/hex/index.js';
import { hsl } from './src/io/hsl/index.js';
import { lab } from './src/io/lab/index.js';
import { oklab } from './src/io/oklab/index.js';
import { rgb } from './src/io/rgb/index.js';

// operators --> modify existing Colors
import './src/ops/alpha.js';
import './src/ops/darken.js';
import './src/ops/get.js';
import './src/ops/mix.js';
import './src/ops/set.js';
import './src/ops/shade.js';

// interpolators
import './src/interpolator/lrgb.js';
import './src/interpolator/oklab.js';

// generators -- > create new colors
import mix from './src/generator/mix.js';

// other utility methods
import valid from './src/utils/valid.js';
import Color from './src/Color.js';

Object.assign(chroma, {
    Color,
    valid,
    css,
    hex,
    hsl,
    lab,
    oklab,
    rgb,
    mix,
    interpolate: mix
});

export default chroma;

{
  "name": "chroma-js",
  "description": "JavaScript library for color conversions",
  "version": "3.1.2",
  "packageManager": "pnpm@9.6.0",
  "author": "Gregor Aisch",
  "type": "module",
  "homepage": "https://github.com/gka/chroma.js",
  "keywords": [
    "color"
  ],
  "maintainers": [
    {
      "name": "Gregor Aisch",
      "email": "mail@driven-by-data.net",
      "web": "https://vis4.net"
    }
  ],
  "bugs": "https://github.com/gka/chroma.js/issues",
  "repository": {
    "type": "git",
    "url": "git://github.com/gka/chroma.js.git"
  },
  "exports": {
    ".": {
      "import": "./index.js",
      "require": "./dist/chroma.cjs"
    },
    "./light": {
      "import": "./index-light.js",
      "require": "./dist/chroma-light.cjs"
    },
    "./src/colors/*.js": {
      "import": "./src/colors/*.js"
    },
    "./src/generator/*.js": {
      "import": "./src/generator/*.js"
    },
    "./src/interpolator/*.js": {
      "import": "./src/interpolator/*.js"
    },
    "./src/io/*.js": {
      "import": "./src/io/*.js"
    },
    "./src/ops/*.js": {
      "import": "./src/ops/*.js"
    },
    "./src/utils/*.js": {
      "import": "./src/utils/*.js"
    }
  },
  "main": "./index.js",
  "scripts": {
    "prepublishOnly": "npm test -- --run && node .bin/update-version.cjs && npm run build",
    "build": "rollup -c && cross-env DEV=1 rollup -c ",
    "docs": "cd docs && make",
    "docs-preview": "cd docs && make && make preview",
    "test": "vitest",
    "test:update": "vitest -u",
    "lint": "prettier --check index.js index.umd.js index.umd.light.js src *.config.js test && eslint index.js index.umd.js index.umd.light.js src",
    "format": "prettier --write index.js index.umd.js index.umd.light.js src *.config.js test",
    "prepare": "husky"
  },
  "devDependencies": {
    "@babel/eslint-parser": "^7.25.8",
    "@eslint/eslintrc": "^3.1.0",
    "@eslint/js": "^9.12.0",
    "@rollup/plugin-buble": "^1.0.3",
    "@rollup/plugin-terser": "^0.4.4",
    "cross-env": "^7.0.3",
    "eslint": "^9.12.0",
    "globals": "^15.11.0",
    "http-server": "^14.1.1",
    "husky": "^9.1.6",
    "markdown-to-html": "0.0.13",
    "minimatch": "^10.0.1",
    "prettier": "^3.3.3",
    "rollup": "^4.24.0",
    "rollup-plugin-license": "^3.5.3",
    "vitest": "^2.1.2"
  },
  "license": "(BSD-3-Clause AND Apache-2.0)",
  "spm": {
    "main": "chroma.js",
    "ignore": [
      "src",
      "doc",
      "test"
    ]
  }
}

