{
  "name": "webui-aria2",
  "description":
    "This project is to create the worlds best and hottest interface to interact with aria2.",
  "repository": "https://github.com/ziahamza/webui-aria2",
  "keywords": ["AngularJS", "aria2", "static"],
  "buildpacks": [
    {
      "url": "https://github.com/heroku/heroku-buildpack-static"
    }
  ]
}

var http = require("http"),
  url = require("url"),
  path = require("path"),
  fs = require("fs");
port = process.argv[2] || 8888;

http
  .createServer(function(request, response) {
    var uri = url.parse(request.url).pathname,
      filename = path.join(process.cwd(), "docs", uri);

    var extname = path.extname(filename);
    var contentType = "text/html";
    switch (extname) {
      case ".js":
        contentType = "text/javascript";
        break;
      case ".css":
        contentType = "text/css";
        break;
      case ".ico":
        contentType = "image/x-icon";
        break;
      case ".svg":
        contentType = "image/svg+xml";
        break;
    }

    fs.exists(filename, function(exists) {
      if (!exists) {
        response.writeHead(404, { "Content-Type": "text/plain" });
        response.write("404 Not Found\n");
        response.end();
        return;
      }

      if (fs.statSync(filename).isDirectory()) filename += "/index.html";

      fs.readFile(filename, "binary", function(err, file) {
        if (err) {
          response.writeHead(500, { "Content-Type": "text/plain" });
          response.write(err + "\n");
          response.end();
          return;
        }
        response.writeHead(200, { "Content-Type": contentType });
        response.write(file, "binary");
        response.end();
      });
    });
  })
  .listen(parseInt(port, 10));

console.log("WebUI Aria2 Server is running on http://localhost:" + port);

{
  "name": "webui-aria2",
  "version": "1.0.0",
  "description": "The aim for this project is to create the worlds best and hottest interface to interact with aria2.",
  "homepage": "https://github.com/ziahamza/webui-aria2#readme",
  "license": "MIT",
  "scripts": {
    "dev": "webpack -d --watch --progress & node node-server",
    "build": "webpack -p --progress",
    "analyze": "webpack -p --profile --json > stats.json && webpack-bundle-analyzer stats.json ./docs -p 9999",
    "format": "prettier -l --write  \"**/*{js,scss}\" "
  },
  "repository": {
    "type": "git",
    "url": "git+https://github.com/ziahamza/webui-aria2.git"
  },
  "bugs": {
    "url": "https://github.com/ziahamza/webui-aria2/issues"
  },
  "husky": {
    "hooks": {
      "pre-commit": "pretty-quick --staged"
    }
  },
  "dependencies": {
    "angular": "^1.7.3",
    "angular-translate": "^2.18.1",
    "autoprefixer": "^9.1.3",
    "bootstrap-sass": "^3.3.7",
    "clean-webpack-plugin": "^0.1.19",
    "css-loader": "^1.0.0",
    "file-loader": "^2.0.0",
    "flag-icon-css": "^3.0.0",
    "html-webpack-plugin": "^3.2.0",
    "jquery": "^3.3.1",
    "lodash": "^4.17.10",
    "mini-css-extract-plugin": "^0.4.2",
    "node-sass": "^4.9.3",
    "postcss-loader": "^3.0.0",
    "sass-loader": "^7.1.0",
    "webpack": "^4.17.1",
    "webpack-bundle-analyzer": "^2.13.1",
    "webpack-cli": "^3.1.0",
    "workbox-webpack-plugin": "^3.4.1"
  },
  "devDependencies": {
    "husky": "^1.0.0-rc.13",
    "prettier": "1.14.2",
    "pretty-quick": "^1.6.0"
  }
}

const path = require("path");
const webpack = require("webpack");

const MiniCssExtractPlugin = require("mini-css-extract-plugin");
const HtmlWebpackPlugin = require("html-webpack-plugin");
const CleanWebpackPlugin = require("clean-webpack-plugin");
const WorkboxPlugin = require("workbox-webpack-plugin");

const BUILD_DIR = path.join(__dirname, "docs");
const APP_DIR = path.join(__dirname, "src", "js");

const config = {
  entry: {
    app: APP_DIR + "/app.js"
  },
  module: {
    rules: [
      {
        test: /\.scss$/,
        use: [MiniCssExtractPlugin.loader, "css-loader", "postcss-loader", "sass-loader"]
      },
      {
        test: /\.css$/,
        use: [MiniCssExtractPlugin.loader, "css-loader", "postcss-loader"]
      },
      {
        test: /\.svg$/,
        use: [
          {
            loader: "file-loader",
            options: {
              name: "[name].[ext]",
              outputPath: "flags/"
            }
          }
        ]
      }
    ]
  },
  output: {
    path: BUILD_DIR,
    filename: "[name].js"
  },
  plugins: [
    new CleanWebpackPlugin(["docs"]),
    new webpack.ProvidePlugin({
      "window.jQuery": "jquery",
      jQuery: "jquery",
      $: "jquery"
    }),
    new HtmlWebpackPlugin({
      template: "src/index-template.html",
      inject: "head"
    }),
    new MiniCssExtractPlugin({
      filename: "[name].css"
    }),
    new WorkboxPlugin.GenerateSW()
  ],
  optimization: {
    splitChunks: {
      cacheGroups: {
        commons: { test: /[\\/]node_modules[\\/]/, name: "vendor", chunks: "all" }
      }
    }
  },
  resolve: {
    modules: [
      path.resolve("./"),
      path.resolve("./node_modules"),
      path.resolve("./src/js"),
      path.resolve("./src/scss")
    ]
  }
};

module.exports = config;
