install:
	@bundle install
	@npm i -g gh-deploy
	@gem install jill
	@pip install -r requirements.txt
docs:
	@echo making docs
	@rm -rf docs
	@mkdir docs
	@cp *.md docs/
	@cp README.md docs/index.md
	@cp extra.css docs/
	@cp icon.svg docs/
	@cp arn.svg docs/
	@cp CNAME docs/
	@echo done

publish: docs
	@mkdocs gh-deploy --clean
	@rm -rf docs

serve: docs
	@mkdocs serve



.PHONY: docs publish serve check stars install

module.exports = {
    publishers: {
        file: true,
        github: {
            auth: {
                type: 'token',
                token: process.env.CELESTE_GH
            },
            context: {
                ref: process.env.TRAVIS_PULL_REQUEST_SHA,
                repo: 'awesome-react-native',
                owner: 'jondot',
                defaultCommitMessage: 'Celeste: update'
            }
        }
    },
    plugins: {
        dedupLinks: true,
        brokenLinks: true,
        sortByStars: true,
    }
}

module.exports = {
  plugins: {
    dedupLinks: true,
    brokenLinks: true,
    fetchStars: true,
    sortByStars: true,
    toc: true
  }
}

{
  "scripts": {
    "ci:pr": "celeste -c celeste-config-pr.js -i README.md --output README.md",
    "ci:weekly": "celeste -c celeste-config-weekly.js -i README.md --output README.md",
    "refresh-site": "make docs && make publish"
  },
  "dependencies": {
    "celeste": "^0.10.0"
  }
}
