{
  "env": {
    "browser": true,
    "es2021": true,
    "mocha": true
  },
  "extends": ["airbnb-base", "plugin:prettier/recommended"],
  "parserOptions": {
    "ecmaVersion": 12,
    "sourceType": "module"
  },
  "plugins": ["prettier"],
  "rules": {
    "prettier/prettier": "error",
    "prefer-const": "off",
    "camelcase": "off",
    "no-underscore-dangle": "off",
    "consistent-return": "off",
    /* Remove the necessity to use this on classes */
    "class-methods-use-this": "off",
    /* Enable variables declarations from shadowing variables declared in the outer scope */
    "no-shadow": "off"
  }
}

{
  "name": "zenorocha/clipboardjs",
  "description": "Modern copy to clipboard. No Flash. Just 3kb gzipped https://clipboardjs.com",
  "type": "component",
  "homepage": "https://clipboardjs.com/",
  "authors": [
    {
      "name": "Zeno Rocha",
      "homepage": "http://zenorocha.com/"
    }
  ],
  "require": {
    "oomphinc/composer-installers-extender": "*"
  },
  "extra": {
    "component": {
      "scripts": [
        "dist/clipboard.js"
      ],
      "files": [
        "dist/clipboard.min.js"
      ]
    }
  }
}

var webpackConfig = require('./webpack.config.js');

module.exports = function (karma) {
  karma.set({
    plugins: [
      'karma-webpack',
      'karma-chai',
      'karma-sinon',
      'karma-mocha',
      'karma-chrome-launcher',
    ],

    frameworks: ['chai', 'sinon', 'mocha', 'webpack'],

    files: [
      { pattern: 'src/**/*.js', watched: false },
      { pattern: 'test/**/*.js', watched: false },
    ],

    preprocessors: {
      'src/**/*.js': ['webpack'],
      'test/**/*.js': ['webpack'],
    },

    webpack: {
      module: webpackConfig.module,
      plugins: webpackConfig.plugins,
    },

    webpackMiddleware: {
      stats: 'errors-only',
    },

    browsers: ['ChromeHeadless'],
  });
};

