const path = require('path');
const gulp = require('gulp');
const uglify = require('gulp-uglify');
const cleanCSS = require('gulp-clean-css');
const concat = require('gulp-concat');
const replace = require('gulp-replace');
const header = require('gulp-header');
const sourcemaps = require('gulp-sourcemaps');
const zip = require('gulp-zip');
const del = require('del');
const minimist = require('minimist');
const yargs = require('yargs');
const pkg = require('./package.json');

// åŸºç¡€é…ç½®
const config = {
  // å¤´éƒ¨æ³¨é‡Š
  comment: [
    '/** v<%= pkg.version %> | <%= pkg.license %> Licensed */<%= js %>',
    {pkg: pkg, js: ';'}
  ],
  // å…¨éƒ¨æ¨¡å—
  modules: 'lay,laytpl,laypage,laydate,jquery,layer,util,dropdown,slider,colorpicker,element,upload,form,table,treeTable,tree,transfer,carousel,rate,flow,code'
};

// è·å–å‚æ•°
const argv = minimist(process.argv.slice(2), {
  default: {
    vs: pkg.version
  }
});

const rlsFileName = `${pkg.name}-v${argv.vs}`; // å‘è¡Œæ–‡ä»¶å
const rlsDest = `./release/zip/${rlsFileName}/${pkg.name}`; // å‘è¡Œç›®æ ‡è·¯å¾„
const rlsDirname = path.dirname(rlsDest); // å‘è¡Œç›®å½•å

// å¤åˆ¶ç›®æ ‡è·¯å¾„
const copyDest = argv.dest
  ? path.join(argv.dest, (argv.vs ? '/' + argv.vs : ''))
: rlsDest;

// æ‰“åŒ…ç›®æ ‡è·¯å¾„
const dest = './dist';

// js
const js = () => {
  let src = [
    './src/**/{layui,layui.all,'+ config.modules +'}.js'
  ];
  return gulp.src(src)
  .pipe(sourcemaps.init())
  .pipe(uglify({
    output: {
      ascii_only: true // escape Unicode characters in strings and regexps
    },
    ie: true
  }))
  .pipe(concat('layui.js', {newLine: ''}))
  .pipe(header.apply(null, config.comment))
  .pipe(sourcemaps.write('.'))
  .pipe(gulp.dest(dest));
};

// css
const css = () => {
  let src = [
    './src/css/**/{layui,*}.css'
  ];
  return gulp.src(src)
  .pipe(sourcemaps.init())
  .pipe(cleanCSS({
    compatibility: 'ie8'
  }))
  .pipe(concat('layui.css', {newLine: ''}))
  .pipe(sourcemaps.write('.'))
  .pipe(gulp.dest(dest +'/css'));
};

// files
const files = () => {
  let src = ['./src/**/*.{eot,svg,ttf,woff,woff2,html,json,png,jpg,gif}'];
  return gulp.src(src)
  .pipe(gulp.dest(dest));
};

// clean
const clean = () => {
  return del([dest]);
};

// é»˜è®¤ä»»åŠ¡
exports.default = gulp.series(clean, gulp.parallel(js, css, files));

// å¤åˆ¶ dist ç›®å½•åˆ°æŒ‡å®šè·¯å¾„
exports.cp = gulp.series(() => del(copyDest), () => {
  const src = `${dest}/**/*`;

  // å¤åˆ¶ css js
  gulp.src(`${src}.{css,js}`)
  .pipe(replace(/\n\/(\*|\/)\#[\s\S]+$/, '')) // è¿‡æ»¤ css,js çš„ map ç‰¹å®šæ³¨é‡Š
  .pipe(gulp.dest(copyDest));

  // å¤åˆ¶å…¶ä»–æ–‡ä»¶
  return gulp.src([
    src,
    `!${src}.{css,js,map}` // è¿‡æ»¤ map æ–‡ä»¶
  ])
  .pipe(replace(/\n\/(\*|\/)\#[\s\S]+$/, '')) // è¿‡æ»¤ css,js çš„ map ç‰¹å®šæ³¨é‡Š
  .pipe(gulp.dest(copyDest));
});

// å‘è¡Œ
exports.release = gulp.series(
  () => del([rlsDirname]), // æ¸…ç†å‘è¡Œç›®å½•
  () => { // ç”Ÿæˆè¯´æ˜
    return gulp.src('./release/introduce/**/*')
    .pipe(replace(/[^'"]+(\/layui\.css)/, 'layui/css$1')) // æ›¿æ¢ css å¼•å…¥è·¯å¾„ä¸­çš„æœ¬åœ° path
    .pipe(replace(/[^'"]+(\/layui\.js)/, 'layui$1')) // æ›¿æ¢ js å¼•å…¥è·¯å¾„ä¸­çš„æœ¬åœ° path
    .pipe(gulp.dest(rlsDirname)) // ç”¨äºæœ¬åœ°
    .pipe(gulp.dest('./examples/introduce')); // ç”¨äº Github actions
  },
  exports.cp, // å¤åˆ¶ dist ç›®å½•æ–‡ä»¶
  () => { // ç”Ÿæˆ ZIP å‹ç¼©åŒ…
    const base = path.dirname(rlsDirname);
    return gulp.src(rlsDirname + '/**/*', {
      base: base
    })
    .pipe(zip(`${rlsFileName}.zip`))
    .pipe(gulp.dest(base))
  }
);

// helper
exports.help = () => {
  let usage = '\nUsage: gulp [options] tasks';
  let parser = yargs.usage(usage, {
    dest: {
      type: 'string',
      desc: 'è‡ªå®šä¹‰è¾“å‡ºè·¯å¾„'
    },
    vs: {
      type: 'boolean',
      desc: 'ç”Ÿæˆä¸€ä¸ªå¸¦ç‰ˆæœ¬å·çš„æ–‡ä»¶å¤¹'
    }
  });

  parser.showHelp(console.log);
  console.log([
    'Tasks:',
    '  default  é»˜è®¤ä»»åŠ¡',
    '  release  å‘è¡Œä»»åŠ¡',
    '  cp  å°† dist ç›®å½•å¤åˆ¶ä¸€ä»½åˆ°å‚æ•° --dest æŒ‡å‘çš„ç›®å½•'
  ].join('\n'), '\n\nExamples:\n  gulp cp --dest ./v', '\n');
  return gulp.src('./');
};
