{
  "parser": "babel-eslint",
  "extends": ["@sampotts/eslint-config/es6"],
  "env": {
    "browser": true,
    "es6": true
  },
  "globals": {
    "Plyr": false,
    "jQuery": false
  },
  "rules": {
    "import/no-cycle": "warn"
  },
  "parserOptions": {
    "sourceType": "module"
  }
}

{
  "plugins": ["stylelint-selector-bem-pattern"],
  "extends": ["stylelint-config-sass-guidelines", "stylelint-config-prettier"],
  "customSyntax": "postcss-scss",
  "rules": {
    "selector-class-pattern": null,
    "selector-no-qualifying-type": [
      true,
      {
        "ignore": ["attribute", "class"]
      }
    ],
    "max-nesting-depth": 4,
    "plugin/selector-bem-pattern": {
      "preset": "bem",
      "componentName": "(([a-z0-9]+(?!-$)-?)+)",
      "componentSelectors": {
        "initial": "\\.{componentName}(((__|--)(([a-z0-9\\[\\]'=]+(?!-$)-?)+))+)?$"
      },
      "ignoreSelectors": [".*\\.has-.*", ".*\\.is-.*"]
    }
  }
}

{
    "js": {
        "plyr.js": {
            "src": "./src/js/plyr.js",
            "dist": "./dist/",
            "formats": ["es", "umd"],
            "namespace": "Plyr"
        },
        "plyr.polyfilled.js": {
            "src": "./src/js/plyr.polyfilled.js",
            "dist": "./dist/",
            "formats": ["es", "umd"],
            "namespace": "Plyr",
            "polyfill": true
        },
        "demo.js": {
            "src": "./demo/src/js/demo.js",
            "dist": "./demo/dist/",
            "formats": ["iife"],
            "namespace": "Demo",
            "polyfill": true
        }
    },
    "css": {
        "plyr.css": {
            "src": "./src/sass/plyr.scss",
            "dist": "./dist/"
        },
        "demo.css": {
            "src": "./demo/src/sass/bundles/demo.scss",
            "dist": "./demo/dist/"
        },
        "error.css": {
            "src": "./demo/src/sass/bundles/error.scss",
            "dist": "./demo/dist/"
        }
    },
    "sprite": {
        "plyr.svg": {
            "src": "./src/sprite/*.svg",
            "dist": "./dist"
        },
        "demo.svg": {
            "src": "./src/sprite/*.svg",
            "dist": "./demo/dist"
        }
    }
}

{
  "cdn": {
    "bucket": "plyr",
    "domain": "cdn.plyr.io",
    "region": "us-east-1"
  },
  "demo": {
    "bucket": "plyr.io",
    "domain": "plyr.io",
    "region": "us-west-1"
  }
}

{
  "folders": [
    {
      "path": "."
    }
  ],
  "settings": {
    "search.exclude": {
      "**/node_modules": true,
      "**/dist": true
    },

    // Linting
    "stylelint.enable": true,
    "stylelint.validate": ["css", "scss"],
    "css.validate": false,
    "less.validate": false,
    "scss.validate": false,
    "javascript.validate.enable": false,

    // Formatting
    "editor.defaultFormatter": "esbenp.prettier-vscode",
    "editor.tabSize": 2,
    "editor.insertSpaces": true,
    "editor.formatOnSave": true,

    // Trim on save
    "files.trimTrailingWhitespace": true,

    // Special file associations
    "files.associations": {
      ".eslintrc": "jsonc"
    },

    "editor.codeActionsOnSave": {
      "source.fixAll": true
    }
  }
}
