module.exports = {
	'*.md': ['prettier --write'],
	'*.{ts,js}': ['eslint --fix --cache']
};

{
  "exclude": ["test", "native.js"],
  "extension": [".ts", ""]
}

{
	"arrowParens": "avoid",
	"printWidth": 100,
	"singleQuote": true,
	"trailingComma": "none",
	"useTabs": true,
	"overrides": [
		{
			"files": "**/*.md",
			"options": {
				"printWidth": 76,
				"tabWidth": 2,
				"proseWrap": "never"
			}
		}
	],
	"plugins": ["prettier-plugin-organize-imports"]
}

{
  "decisions": {},
  "rules": {},
  "version": 1
}

import eslint from '@eslint/js';
import eslintPluginPrettierRecommended from 'eslint-plugin-prettier/recommended';
import eslintPluginUnicorn from 'eslint-plugin-unicorn';
import pluginVue from 'eslint-plugin-vue';
import globals from 'globals';
import tseslint from 'typescript-eslint';

export default tseslint.config(
	eslint.configs.recommended,
	...tseslint.configs.strict,
	...tseslint.configs.stylistic,
	...pluginVue.configs['flat/recommended'],
	{
		ignores: [
			'**/node_modules',
			'**/dist',
			'**/perf',
			'**/tmp',
			'**/coverage',
			'**/_tmp',
			'**/cache',
			'**/native.d.ts',
			'rust',
			'test/*/samples/**/*.*',
			'!test/*/samples/**/_config.js',
			'!test/*/samples/**/rollup.config.js',
			'!**/.vitepress',
			'wasm/',
			'wasm-node/'
		]
	},
	{
		languageOptions: {
			globals: {
				...globals.browser,
				...globals.node
			}
		},
		plugins: {
			unicorn: eslintPluginUnicorn
		},
		rules: {
			'@typescript-eslint/consistent-type-assertions': [
				'error',
				{
					assertionStyle: 'as',
					objectLiteralTypeAssertions: 'allow'
				}
			],
			'@typescript-eslint/consistent-type-definitions': ['error', 'interface'],
			'@typescript-eslint/consistent-type-imports': 'error',
			'@typescript-eslint/member-ordering': [
				'error',
				{
					default: {
						memberTypes: [],
						order: 'alphabetically'
					}
				}
			],
			'@typescript-eslint/no-dynamic-delete': 'off',
			'@typescript-eslint/no-empty-function': 'off',
			'@typescript-eslint/no-explicit-any': 'off',
			'@typescript-eslint/no-invalid-void-type': 'off',
			'@typescript-eslint/no-non-null-assertion': 'off',
			'@typescript-eslint/no-unused-vars': [
				'error',
				{
					argsIgnorePattern: '^_',
					ignoreRestSiblings: true,
					varsIgnorePattern: '^_'
				}
			],
			'@typescript-eslint/prefer-for-of': 'off',
			'@typescript-eslint/prefer-literal-enum-member': 'off',
			'dot-notation': 'error',
			'no-constant-condition': [
				'error',
				{
					checkLoops: false
				}
			],
			'no-prototype-builtins': 'off',
			'object-shorthand': 'error',
			'prefer-const': [
				'error',
				{
					destructuring: 'all'
				}
			],
			'prefer-object-spread': 'error',
			'sort-imports': [
				'error',
				{
					ignoreCase: true,
					ignoreDeclarationSort: true,
					ignoreMemberSort: false
				}
			],
			'sort-keys': [
				'error',
				'asc',
				{
					caseSensitive: false
				}
			],
			'unicorn/consistent-destructuring': 'off',
			'unicorn/filename-case': 'off',
			'unicorn/no-array-callback-reference': 'off',
			'unicorn/no-array-reduce': 'off',
			'unicorn/no-await-expression-member': 'off',
			'unicorn/no-empty-file': 'off',
			'unicorn/no-for-loop': 'off',
			'unicorn/no-nested-ternary': 'off',
			'unicorn/no-null': 'off',
			'unicorn/no-this-assignment': 'off',
			'unicorn/no-useless-undefined': 'off',
			'unicorn/number-literal-case': 'off',
			'unicorn/prefer-at': 'off',
			'unicorn/prefer-code-point': 'off',
			'unicorn/prefer-math-trunc': 'off',
			'unicorn/prefer-number-properties': 'off',
			'unicorn/prefer-string-raw': 'off',
			'unicorn/prefer-string-replace-all': 'off',
			'unicorn/prefer-structured-clone': 'off',
			'unicorn/prefer-top-level-await': 'off',
			'unicorn/prevent-abbreviations': [
				'error',
				{
					replacements: {
						dir: false
					}
				}
			]
		}
	},
	{
		files: ['**/*.js'],
		rules: {
			'@typescript-eslint/explicit-module-boundary-types': 'off',
			'@typescript-eslint/no-require-imports': 'off',
			'unicorn/no-process-exit': 'off',
			'unicorn/prefer-module': 'off'
		}
	},
	{
		files: ['./*.ts', 'cli/**/*.ts'],
		rules: {
			'unicorn/no-process-exit': 'off'
		}
	},
	{
		files: ['**/*.js', 'cli/**/*.ts'],
		rules: {
			'@typescript-eslint/no-var-requires': 'off'
		}
	},
	{
		files: ['test/**/*.js'],
		languageOptions: {
			globals: {
				...globals.mocha
			}
		},
		rules: {
			'sort-keys': 'off'
		}
	},
	{
		files: ['docs/repl/examples/**/*.js'],
		rules: {
			'no-undef': 'off',
			'unicorn/prevent-abbreviations': 'off'
		}
	},
	{
		files: ['test/**/_config.js'],
		rules: {
			'no-undef': 'off'
		}
	},
	{
		files: ['*.vue', '**/*.vue'],
		languageOptions: {
			parserOptions: {
				parser: '@typescript-eslint/parser'
			}
		}
	},
	eslintPluginPrettierRecommended
);

{
  "default": false,
  "blanks-around-fences": true
}

/* tslint:disable */
/* eslint-disable */

/* auto-generated by NAPI-RS */

export declare function parse(code: string, allowReturnOutsideFunction: boolean, jsx: boolean): Buffer
export declare function parseAsync(code: string, allowReturnOutsideFunction: boolean, jsx: boolean, signal?: AbortSignal | undefined | null): Promise<Buffer>
export declare function xxhashBase64Url(input: Uint8Array): string
export declare function xxhashBase36(input: Uint8Array): string
export declare function xxhashBase16(input: Uint8Array): string

const { existsSync } = require('node:fs');
const path = require('node:path');
const { platform, arch, report } = require('node:process');

const isMusl = () => !report.getReport().header.glibcVersionRuntime;

const bindingsByPlatformAndArch = {
	android: {
		arm: { base: 'android-arm-eabi' },
		arm64: { base: 'android-arm64' }
	},
	darwin: {
		arm64: { base: 'darwin-arm64' },
		x64: { base: 'darwin-x64' }
	},
	freebsd: {
		arm64: { base: 'freebsd-arm64' },
		x64: { base: 'freebsd-x64' }
	},
	linux: {
		arm: { base: 'linux-arm-gnueabihf', musl: 'linux-arm-musleabihf' },
		arm64: { base: 'linux-arm64-gnu', musl: 'linux-arm64-musl' },
		loong64: { base: 'linux-loongarch64-gnu', musl: null },
		ppc64: { base: 'linux-powerpc64le-gnu', musl: null },
		riscv64: { base: 'linux-riscv64-gnu', musl: null },
		s390x: { base: 'linux-s390x-gnu', musl: null },
		x64: { base: 'linux-x64-gnu', musl: 'linux-x64-musl' }
	},
	win32: {
		arm64: { base: 'win32-arm64-msvc' },
		ia32: { base: 'win32-ia32-msvc' },
		x64: { base: 'win32-x64-msvc' }
	}
};

const msvcLinkFilenameByArch = {
	arm64: 'vc_redist.arm64.exe',
	ia32: 'vc_redist.x86.exe',
	x64: 'vc_redist.x64.exe'
};

const packageBase = getPackageBase();
const localName = `./rollup.${packageBase}.node`;
const requireWithFriendlyError = id => {
	try {
		return require(id);
	} catch (error) {
		if (
			platform === 'win32' &&
			error instanceof Error &&
			error.code === 'ERR_DLOPEN_FAILED' &&
			error.message.includes('The specified module could not be found')
		) {
			const msvcDownloadLink = `https://aka.ms/vs/17/release/${msvcLinkFilenameByArch[arch]}`;
			throw new Error(
				`Failed to load module ${id}. ` +
					'Required DLL was not found. ' +
					'This error usually happens when Microsoft Visual C++ Redistributable is not installed. ' +
					`You can download it from ${msvcDownloadLink}`,
				{ cause: error }
			);
		}

		throw new Error(
			`Cannot find module ${id}. ` +
				`npm has a bug related to optional dependencies (https://github.com/npm/cli/issues/4828). ` +
				'Please try `npm i` again after removing both package-lock.json and node_modules directory.',
			{ cause: error }
		);
	}
};

const { parse, parseAsync, xxhashBase64Url, xxhashBase36, xxhashBase16 } = requireWithFriendlyError(
	existsSync(path.join(__dirname, localName)) ? localName : `@rollup/rollup-${packageBase}`
);

function getPackageBase() {
	const imported = bindingsByPlatformAndArch[platform]?.[arch];
	if (!imported) {
		throwUnsupportedError(false);
	}
	if ('musl' in imported && isMusl()) {
		return imported.musl || throwUnsupportedError(true);
	}
	return imported.base;
}

function throwUnsupportedError(isMusl) {
	throw new Error(
		`Your current platform "${platform}${isMusl ? ' (musl)' : ''}" and architecture "${arch}" combination is not yet supported by the native Rollup build. Please use the WASM build "@rollup/wasm-node" instead.

The following platform-architecture combinations are supported:
${Object.entries(bindingsByPlatformAndArch)
	.flatMap(([platformName, architectures]) =>
		Object.entries(architectures).flatMap(([architectureName, { musl }]) => {
			const name = `${platformName}-${architectureName}`;
			return musl ? [name, `${name} (musl)`] : [name];
		})
	)
	.join('\n')}

If this is important to you, please consider supporting Rollup to make a native build for your platform and architecture available.`
	);
}

module.exports.parse = parse;
module.exports.parseAsync = parseAsync;
module.exports.xxhashBase64Url = xxhashBase64Url;
module.exports.xxhashBase36 = xxhashBase36;
module.exports.xxhashBase16 = xxhashBase16;

const {
	parse,
	xxhashBase64Url,
	xxhashBase36,
	xxhashBase16
} = require('./wasm-node/bindings_wasm.js');

exports.parse = parse;
exports.parseAsync = async (code, allowReturnOutsideFunction, _signal) =>
	parse(code, allowReturnOutsideFunction);
exports.xxhashBase64Url = xxhashBase64Url;
exports.xxhashBase36 = xxhashBase36;
exports.xxhashBase16 = xxhashBase16;

