{
  "name"        : "semantic",
  "description" : "Semantic empowers designers and developers by creating a shared vocabulary for UI.",
  "homepage"    : "http://www.semantic-ui.com",
  "author": {
    "name" : "Jack Lukic",
    "web"  : "http://www.jacklukic.com"
  },
  "dependencies": {
    "jquery"      : ">=1.8"
  },
  "main": [
    "src/semantic.less",
    "dist/semantic.js"
  ],
  "keywords": [
    "semantic",
    "ui",
    "css3",
    "framework"
  ],
  "license" : "MIT",
  "ignore": [
    "src/site",
    "test"
  ]
}

{
  "name"        : "semantic/ui",
  "description" : "Semantic empowers designers and developers by creating a shared vocabulary for UI.",
  "homepage"    : "http://www.semantic-ui.com",
  "authors"     : [
    {
      "name"     : "Jack Lukic",
      "email"    : "jacklukic@gmail.com",
      "homepage" : "http://www.jacklukic.com",
      "role"     : "Creator"
    }
  ],
  "support": {
    "issues": "https://github.com/Semantic-Org/Semantic-UI/issues",
    "source": "https://github.com/Semantic-Org/Semantic-UI"
  },
  "keywords" : [
    "semantic",
    "ui",
    "css",
    "framework"
  ],
  "license" : "MIT"
}

/*******************************
            Set-up
*******************************/

let
  gulp         = require('gulp'),

  // read user config to know what task to load
  config       = require('./tasks/config/user'),

  // watch for file changes and build
  watch        = require('./tasks/watch'),

  // build all files
  build        = require('./tasks/build'),
  buildJS      = require('./tasks/build/javascript'),
  buildCSS     = require('./tasks/build/css'),
  buildAssets  = require('./tasks/build/assets'),

  // utility tasks
  clean        = require('./tasks/clean'),
  version      = require('./tasks/version'),

  // install tasks
  install      = require('./tasks/install'),
  checkInstall = require('./tasks/check-install'),

  // docs tasks
  serveDocs    = require('./tasks/docs/serve'),
  buildDocs    = require('./tasks/docs/build'),

  // rtl
  buildRTL     = require('./tasks/rtl/build'),
  watchRTL     = require('./tasks/rtl/watch')
;

/* Simple Compatibility Fix for Gulp 3 Style Tasks */
gulp.start = function(name) {
  let task = gulp.task(name);
  if(task) {
    task();
  }
}

/*******************************
             Tasks
*******************************/

gulp.task('watch', watch);

gulp.task('build', build);
gulp.task('build-javascript', buildJS);
gulp.task('build-css', buildCSS);
gulp.task('build-assets', buildAssets);

gulp.task('clean', clean);
gulp.task('version', version);

gulp.task('install', install);
gulp.task('check-install', checkInstall);


gulp.task('default', checkInstall);

/*--------------
      Docs
---------------*/

/*
  Lets you serve files to a local documentation instance
  https://github.com/Semantic-Org/Semantic-UI-Docs/
*/

gulp.task('serve-docs', serveDocs);
gulp.task('build-docs', buildDocs);


/*--------------
      RTL
---------------*/

if(config.rtl) {
  gulp.task('watch-rtl', watchRTL);
  gulp.task('build-rtl', buildRTL);
}

/* Admin Tasks */
if(config.admin) {
  require('./tasks/collections/admin')(gulp);
}

// Karma configuration
module.exports = function(config) {
  config.set({

    // base path, that will be used to resolve files and exclude
    basePath: '',

    // frameworks to use
    frameworks: [
      'jasmine'
    ],

    // list of files / patterns to load in the browser
    files: [
      // require jquery
      'server/files/javascript/library/jquery.js',
      // read css from compiled css
      'docs/build/uncompressed/**/*.css',
      // read js from src js
      'src/definitions/**/*.js',
      // require helpers
      'test/helpers/*.js',
      // require fixtures
      {
        pattern  : 'test/fixtures/*.html',
        included : false,
        served   : true
      },
      // require spec
      'test/modules/module.spec.js',
      // require tests
      'test/modules/*.js'
    ],

    // list of files to exclude
    exclude: [
      '**/*.swp',
      'karma.conf.js'
    ],

    preprocessors: {
      '**/*.html': [],
      'src/definitions/**/*.js': 'coverage'
    },

    // test results reporter to use
    // possible values: 'dots', 'progress', 'junit', 'growl', 'coverage'
    reporters: ['spec', 'coverage'],

    coverageReporter: {
      type: 'lcov'
    },

    // web server port
    port: 9999,

    // enable / disable colors in the output (reporters and logs)
    colors: true,

    // level of logging
    // possible values: config.LOG_DISABLE || config.LOG_ERROR || config.LOG_WARN || config.LOG_INFO || config.LOG_DEBUG
    logLevel: config.LOG_INFO,

    // enable / disable watching file and executing tests whenever any file changes
    autoWatch: true,

    // Start these browsers, currently available:
    // - Chrome
    // - ChromeCanary
    // - Firefox
    // - Opera
    // - Safari (only Mac)
    // - PhantomJS
    // - IE (only Windows)
    browsers: ['PhantomJS'],


    // If browser does not capture in given timeout [ms], kill it
    captureTimeout: 60000,

    // Continuous Integration mode
    // if true, it capture browsers, run tests and exit
    singleRun: false
  });
};
