/* eslint-env node */

module.exports = {
  arrowParens: "avoid",
  endOfLine: "lf",
  printWidth: 80,
  tabWidth: 2,
  trailingComma: "es5",
};

{
  "plugins": {
    "@release-it/keep-a-changelog": {
      "addUnreleased": true,
      "filename": "CHANGELOG.md"
    }
  },
  "github": {
    "release": false
  },
  "git": {
    "requireBranch": "main",
    "pushRepo": "https://github.com/mozilla/readability.git"
  }
}

/* This Source Code Form is subject to the terms of the Mozilla Public
