// @ts-check

import eslint from '@eslint/js';
import tseslint from 'typescript-eslint';

export default tseslint.config(
  eslint.configs.recommended,
  tseslint.configs.recommended,
  {
    rules: {
      '@typescript-eslint/no-explicit-any': 'off',
      quotes: ['error', 'single'],
      indent: ['error', 2],
    }
  }
);

{
  "name": "live2d-widgets",
  "version": "0.9.0",
  "description": "Live2D widget for web pages",
  "main": "dist/index.js",
  "types": "dist/index.d.ts",
  "type": "module",
  "scripts": {
    "build": "tsc && rollup -c rollup.config.js -f iife",
    "build-dev": "rollup -c rollup.config.js -f iife -o dist/waifu-tips.js -w",
    "eslint": "eslint src/"
  },
  "repository": {
    "type": "git",
    "url": "git+https://github.com/stevenjoezhang/live2d-widget.git"
  },
  "keywords": [
    "Live2d"
  ],
  "author": "stevenjoezhang <stevenjoezhang@gmail.com>",
  "license": "GPL-3.0-or-later",
  "bugs": {
    "url": "https://github.com/stevenjoezhang/live2d-widget/issues"
  },
  "homepage": "https://github.com/stevenjoezhang/live2d-widget#readme",
  "dependencies": {
    "@fortawesome/fontawesome-free": "^6.7.2"
  },
  "devDependencies": {
    "@eslint/js": "^9.17.0",
    "@rollup/plugin-node-resolve": "^16.0.0",
    "@rollup/plugin-terser": "^0.4.4",
    "@rollup/pluginutils": "^5.1.4",
    "@types/node": "^22.10.2",
    "eslint": "^9.17.0",
    "prettier": "^3.4.2",
    "rollup": "^4.29.1",
    "terser": "^5.37.0",
    "typescript": "^5.7.2",
    "typescript-eslint": "^8.18.2"
  }
}

import { nodeResolve } from '@rollup/plugin-node-resolve';
import { createFilter } from '@rollup/pluginutils';
import terser from '@rollup/plugin-terser';

function string(opts = {}) {
  if (!opts.include) {
    throw Error('include option should be specified');
  }

  const filter = createFilter(opts.include, opts.exclude);

  return {
    name: 'string',

    transform(code, id) {
      if (filter(id)) {
        return {
          code: `export default ${JSON.stringify(code)};`,
          map: { mappings: '' },
        };
      }
    },

    renderChunk(code, chunk, outputOptions = {}) {
      return (
        `/*!
 * Live2D Widget
 * https://github.com/stevenjoezhang/live2d-widget
 */
` + code
      );
    },
  };
}

export default {
  input: 'build/index.js',
  output: {
    name: 'live2d_widget',
    file: 'dist/waifu-tips.js',
    format: 'iife',
  },
  plugins: [
    nodeResolve(),
    string({
      include: '**/*.svg',
    }),
    terser(),
  ],
  context: 'this',
};
