  "license": "MIT",
  "bugs": {
    "url": "https://github.com/mrdoob/stats.js/issues"
  },
  "homepage": "https://github.com/mrdoob/stats.js",
  "devDependencies": {
    "rollup": "^2.3.2",
    "google-closure-compiler": "20200224.0.0"
  }
}

export default {
	input: 'src/Stats.js',
	output: [
		{
			format: 'umd',
			name: 'Stats',
			file: 'build/stats.js',
			indent: '\t'
		},
		{
