module.exports = function (api) {
  const env = api.env();
  api.cache.using(() => env === 'development');

  return {
    presets: ['@babel/preset-env', '@babel/preset-react'],
    plugins: [
      '@babel/plugin-transform-runtime',
      // Stage 0
      '@babel/plugin-proposal-function-bind',

      // Stage 1
      '@babel/plugin-proposal-export-default-from',
      '@babel/plugin-proposal-logical-assignment-operators',
      ['@babel/plugin-proposal-optional-chaining', { loose: false }],
      ['@babel/plugin-proposal-pipeline-operator', { proposal: 'minimal' }],
      ['@babel/plugin-proposal-nullish-coalescing-operator', { loose: false }],
      '@babel/plugin-proposal-do-expressions',

      // Stage 2
      ['@babel/plugin-proposal-decorators', { legacy: true }],
      '@babel/plugin-proposal-function-sent',
      '@babel/plugin-proposal-export-namespace-from',
      '@babel/plugin-proposal-numeric-separator',
      '@babel/plugin-proposal-throw-expressions',

      // Stage 3
      '@babel/plugin-syntax-dynamic-import',
      '@babel/plugin-syntax-import-meta',
      ['@babel/plugin-proposal-class-properties', { loose: false }],
      '@babel/plugin-proposal-json-strings',
      'react-hot-loader/babel',
    ],
  };
};

const path = require('path');
const gulp = require('gulp');
const babel = require('gulp-babel');
const postcss = require('gulp-postcss');

const paths = {
  js: {
    src: [
      './components/**/*.js',
      '!./components/**/*.spec.js',
      '!./components/**/__test__',
      '!./components/__mocks__/**/*.js'
    ],
  },
  css: {
    src: [
      './components/*.css',
      './components/**/*.css'
    ],
  },
  tsd: {
    src: './components/**/*.d.ts',
  },
  dest: './lib',
}


function js(cb) {
  gulp.src(paths.js.src)
    .pipe(babel())
    .pipe(gulp.dest(paths.dest));

  cb()
};

function css(cb) {
  // Copied from webpack/postcss.config.js
  const plugins = [
    require('postcss-import')({
      root: path.join(__dirname, '../'),
      path: path.join(__dirname, '../components')
    }),
    require('postcss-mixins'),
    require('postcss-each'),
    require('postcss-apply'),
    require('postcss-preset-env')({
      stage: 0, // required to get all features that were from cssnext
      features: {
        'custom-properties': {
          preserve: false // required to output values instead of variables
        },
        'color-mod-function': true, // required to use color-mod()
      }
    }),
    require('postcss-calc'), // required as postcss-preset-env doesn't have a reduce calc() funtion
    require('postcss-normalize'),
    require('postcss-reporter')({
      clearReportedMessages: true
    })
  ]

  gulp.src(paths.css.src)
    .pipe(postcss(plugins))
    .pipe(gulp.dest(paths.dest));

  cb()
};

function tsd(cb) {
  gulp.src(paths.tsd.src)
    .pipe(gulp.dest(paths.dest));

  cb()
};

const build = gulp.series(js, css, tsd)

gulp.task('default', build)

module.exports = {
  hooks: {
    'pre-commit': 'npm run lint:staged',
  },
};

