files.content:
build:
	@seatools build

site:
	@seatools build
	@seatools site

test: test_node test_local test_http

test_node:
	@node tests/node-runner.js

test_local:
	@seatools site
	@seatools test --local

test_http:
	@seatools site
	@seatools test --http

totoro:
	@seatools site
	@seatools test --totoro

size:
	@seatools size

pages:
	@seatools publish

{
  "family": "seajs",
  "name": "seajs",
  "version": "3.0.3",
  "description": "A Module Loader for the Web",
  "homepage": "http://seajs.org/",
  "keywords": ["module", "loader"],
  "author": "Frank Wang <lifesinger@gmail.com>",
  "engines": {
    "node": ">= 0.12.0"
  },
  "dependencies": {},
  "devDependencies": {
    "seatools": "0.5.12"
  },
  "repository": {
    "type": "git",
    "url": "git://github.com/seajs/seajs.git"
  },
  "main": "./lib/sea.js",
  "spm": {
    "main": "./dist/sea.js"
  },
  "licenses": [
    {
      "type": "MIT",
      "url": "http://seajs.org/LICENSE.md"
    }
  ]
}
