module.exports = function(api) {
  // Cache the returned value forever and don't call this function again.
  if (api) api.cache(true);

  return {
    'presets': [
      '@babel/preset-flow',
      ['@babel/preset-env', {
        'loose': true,
        'targets': {
          'esmodules': true
        }
      }],
      ['@babel/preset-react', {
        'pragma': 'createElement'
      }]
    ],
    'plugins': [
      '@babel/plugin-proposal-export-default-from',
      [
        '@babel/plugin-proposal-class-properties',
        {
          loose: true
        }
      ],
      'babel-plugin-transform-jsx-stylesheet',
      [
        '@babel/plugin-proposal-decorators',
        {
          decoratorsBeforeExport: true
        }
      ],
      '@babel/plugin-syntax-dynamic-import',
    ],
    'ignore': [
      'src/generator/templates',
      '__mockc__',
      'dist'
    ],
  };
};

// jest.config.js

// Note: If you are using babel version 7 you have to install babel-jest with
// yarn add --dev babel-jest @babel/core

module.exports = {
  'collectCoverage': true,
  'verbose': true,
  'setupFiles': [
    './scripts/jest/setupEnvironment.js',
    'jest-localstorage-mock'
  ],
  'setupFilesAfterEnv': ['./scripts/jest/setupTests.js'],
  'moduleNameMapper': {
    // https://jestjs.io/docs/en/webpack#handling-static-assets
    // For CSS Modules
    '\\.css$': '<rootDir>/scripts/jest/styleMock.js',
  },
  'testPathIgnorePatterns': [
    '/node_modules/',
    '/fixtures/',
    '/__modules__/',
    '/__files__/',
    '/lib/',
    '/dist/',
    '/es/',
  ]
};
