module.exports = {
  root: true,
  env: {
    browser: true,
    es2021: true,
    node: true,
    "jest/globals": true,
  },
  extends: [
    "eslint:recommended",
    "plugin:@typescript-eslint/recommended",
    "plugin:react/recommended",
    "plugin:react-hooks/recommended",
  ],
  parser: "@typescript-eslint/parser",
  parserOptions: {
    "ecmaVersion": "latest",
    "sourceType": "module",
  },
  plugins: [
    "@typescript-eslint",
    "react",
    "jest",
  ],
  globals: {
    goog: true,
    i18n: true,
    require: true,
  },
  rules: {
    semi: ["error", "always"],
    "comma-dangle": ["error", "always-multiline"],
    quotes: ["error", "double"],
    "no-prototype-builtins": "off",
    "class-methods-use-this": "error",
    "@typescript-eslint/explicit-function-return-type": "off",
    "@typescript-eslint/no-var-requires": "off",
    "@typescript-eslint/no-explicit-any": "off",
    "@typescript-eslint/no-require-imports": "off",
  },
  overrides: [{
    files: [".eslintrc.{js,cjs}"],
    env: { "node": true },
    parserOptions: { "sourceType": "script" },
  }],
  settings: {
    "import/resolver": {
      "typescript": {},
    },
    "react": {
      "version": "detect",
    },
  },
};

module.exports = function(grunt) {

  // load all tasks from package.json
  require('load-grunt-config')(grunt);
  require('time-grunt')(grunt);
  require('google-closure-compiler').grunt(grunt, {
    platfrom: 'native'
  });

  /**
   * TASKS
   */
  // build everything ready for a commit
  grunt.registerTask('build', ['css', 'translations', 'js']);
  // build translations
  grunt.registerTask('build:translations', ['translations', 'js']);
  // build utils
  grunt.registerTask('build:utils', ['closure-compiler:utils']);
  // just CSS
  grunt.registerTask('css', ['sass', 'cssmin']);
  // just images
  grunt.registerTask('img', ['generate-sprite', 'css']);
  // just javascript
  grunt.registerTask('js', ['shell:eslint', 'shell:genTsDeclaration', 'shell:genReactTsDeclaration', 'shell:buildJs', 'replace', 'react', 'vue']);
  grunt.registerTask('jsfast', ['shell:buildJs', 'replace']);
  // just react
  grunt.registerTask('react', ['replace:reactWithUtils', 'shell:buildReact']);
  // just vue
  grunt.registerTask('vue', ['replace:vueWithUtils', 'shell:buildVue']);

  // Run tests with a server so that async imports/fetches work.
  grunt.registerTask('jasmine:test', ['connect:test', 'jasmine']);
  grunt.registerTask('jasmine:interactive', () => {
    grunt.event.once('connect.test.listening', (host, port) => {
      const origin = `http://${host === '::' ? 'localhost' : host}:${port}`;
      console.log(`
        To view and debug tests in your browser, go to ${origin}/spec.html
        To play with a demo of the package, go to ${origin}/demo.html
      `);
    });
    grunt.task.run('connect:test:keepalive');
  });

  // Travis CI
  grunt.registerTask('travis', ['jasmine:test']);
  // bump version number in 3 files, rebuild js to update headers, then commit, tag and push
  grunt.registerTask('version', ['shell:test', 'bump-only', 'js', 'bump-commit']);
  grunt.registerTask('version:minor', ['shell:test', 'bump-only:minor', 'js', 'bump-commit']);
  grunt.registerTask('version:major', ['shell:test', 'bump-only:major', 'js', 'bump-commit']);

};

/* eslint-disable no-undef */
/* eslint-disable @typescript-eslint/no-var-requires */
const { build } = require("esbuild");
const packageJson = require("./package.json");

const getBanner = (moduleName) =>
  "/*\n" +
  ` * International Telephone Input v${packageJson.version}\n` +
  ` * ${packageJson.repository.url}\n` +
  " * Licensed under the MIT license\n" +
  " */\n\n" +
  // we can remove this UMD hack once it is supported by esbuild: https://github.com/evanw/esbuild/issues/507
  "// UMD\n" +
  "(function(factory) {\n" +
  "  if (typeof module === 'object' && module.exports) {\n" +
  "    module.exports = factory();\n" +
  "  } else {\n" +
  `    window.${moduleName} = factory();\n` +
  "  }\n" +
  "}(() => {\n";

const footer =
  "\n// UMD\n" +
  "  return factoryOutput.default;\n" +
  "}));";

const shared = {
  bundle: true,
  logLevel: "info",
  format: "iife",
  globalName: "factoryOutput",
  footer: {
    js: footer,
  },
  define: {
    "process.env.VERSION": `"${packageJson.version}"`,
  },
};

//* build/js/intlTelInput.js
build({
  ...shared,
  banner: {
    js: getBanner("intlTelInput"),
  },
  entryPoints: ["src/js/intl-tel-input.ts"],
  minify: false,
  outfile: "build/js/intlTelInput.js",
});

//* build/js/intlTelInput.min.js
build({
  ...shared,
  banner: {
    js: getBanner("intlTelInput"),
  },
  entryPoints: ["src/js/intl-tel-input.ts"],
  minify: true,
  outfile: "tmp/built.min.js",
});

//* build/js/data.js
build({
  ...shared,
  banner: {
    js: getBanner("allCountries"),
  },
  entryPoints: ["src/js/intl-tel-input/data.ts"],
  minify: false,
  outfile: "build/js/data.js",
});

//* build/js/data.min.js
build({
  ...shared,
  banner: {
    js: getBanner("allCountries"),
  },
  entryPoints: ["src/js/intl-tel-input/data.ts"],
  minify: true,
  outfile: "build/js/data.min.js",
});

//* build/js/intlTelInputWithUtils.js
build({
  ...shared,
  banner: {
    js: getBanner("intlTelInput"),
  },
  entryPoints: ["src/js/intl-tel-input/intlTelInputWithUtils.ts"],
  minify: false,
  outfile: "build/js/intlTelInputWithUtils.js",
});

//* build/js/intlTelInputWithUtils.min.js
build({
  ...shared,
  banner: {
    js: getBanner("intlTelInput"),
  },
  entryPoints: ["src/js/intl-tel-input/intlTelInputWithUtils.ts"],
  minify: true,
  outfile: "build/js/intlTelInputWithUtils.min.js",
});

//* build/js/i18n
build({
  charset: "utf8",
  entryPoints: ["src/js/intl-tel-input/i18n/**/*.ts"],
  outdir: "build/js/i18n",
});

/** @type {import('jest').Config} */
module.exports = {
  moduleDirectories: [
    "node_modules",
    "build/js",
  ],
  transform: {
    // Most of the build outputs in this project use UMD syntax, but the
    // utilities script is a proper ES Module. Unfortunately, Jest cannot treat
    // a file with a `.js` extension as ESM unless the whole project is ESM
    // (Jest does not use Node.js built-in resolution and loading logic), so
    // we have to set up special parsing for that file.
    // See also: https://jestjs.io/docs/next/ecmascript-modules
    "utils.js$": [
      "babel-jest",
      {
        plugins: [
          "@babel/plugin-transform-modules-commonjs",
          "babel-plugin-add-module-exports",
        ],
      },
    ],
  },
};

