{
  "env": {
    "browser": true,
    "node": true
  },
  "globals": {
    "Buffer": true,
    "escape": true
  },
  "rules": {
    "quotes": [2, "single"],
    "strict": 0,
    "eol-last": ["error", "always"],
    "curly": 0,
    "no-empty": 0,
    "no-underscore-dangle": 0,
    "new-cap": 0,
    "dot-notation": 0,
    "no-use-before-define": 0,
    "semi": [2, "always"],
    "keyword-spacing": [2, {"before": true, "after": true}],
    "space-before-blocks": [2, "always"],
    "no-trailing-spaces": 2,
    "space-unary-ops": 0
  }
}

{
  "name": "aws-sdk",
  "ignore": [
    "apis", "doc-src", "dist-tools", "eslint-rules", "features", "lib",
    "scripts", "tasks", "test", "Gemfile*", "configuration*",
    "Rakefile", "package.json", "testem.json", ".*", "index.js"
  ],
  "main": "dist/aws-sdk.js"
}

require('./lib/browser_loader');

var AWS = require('./lib/core');
if (typeof window !== 'undefined') window.AWS = AWS;
if (typeof module !== 'undefined') module.exports = AWS;
if (typeof self !== 'undefined') self.AWS = AWS;

{
  "region": "us-east-1",
  "s3": {
    "params": { "Bucket": "test-s3-bucket" }
  },
  "devicefarm": {
    "region": "us-west-2"
  }
}

import {GlobalConfigInstance} from './lib/config'; 

export * from './lib/core';
export var config: GlobalConfigInstance

import {GlobalConfigInstance} from './lib/config';

export * from './lib/core';
export * from './clients/all';
export var config: GlobalConfigInstance

export as namespace AWS;

// Karma configuration
// Generated on Wed Feb 08 2017 17:56:27 GMT-0800 (PST)

module.exports = function(config) {
  config.set({
    frameworks: ['jasmine'],
    files: [
      'test/browser/build/aws-sdk-all.js',
      'test/browser/build/tests.js'
    ],
    reporters: ['progress'],
    port: 19876,
    colors: false,
    logLevel: config.LOG_ERROR,
    browsers: [ 'HeadlessChrome' ],
    customLaunchers: {
      HeadlessChrome: {
          base: 'ChromeHeadless',
             flags: [ '--no-sandbox', ],
                  },
            },
    singleRun: true,
    browserDisconnectTolerance: 3,
    browserNoActivityTimeout: 30000,
    concurrency: 1
  })
};

var AWS = require('./lib/react-native-loader');

require('./clients/all');
module.exports = AWS;

{
  "test_page": "test/browser/runner.html"
}
