	@node ./tools/robot-infrequent.js >> $(ZI_DICT_INFREQUENT)
	@echo '};'                        >> $(ZI_DICT_INFREQUENT)
	@node ./tools/combo-dict.js 			 > $(ZI_DICT_WEB)

