{
	"name": "degit",
	"version": "2.8.4",
	"engines": {
		"node": ">=8.0.0"
	},
	"description": "Straightforward project scaffolding",
	"main": "dist/index.js",
	"bin": {
		"degit": "degit"
	},
	"scripts": {
		"lint": "eslint --color --ignore-path .gitignore .",
		"dev": "npm run build -- --watch",
		"build": "rollup -c",
		"test": "mocha",
		"pretest": "npm run build",
		"prepublishOnly": "npm test"
	},
	"repository": {
		"type": "git",
		"url": "git+https://github.com/Rich-Harris/degit.git"
	},
	"keywords": [
		"scaffolding",
		"template",
		"git"
	],
	"author": "Rich Harris",
	"license": "MIT",
	"bugs": {
		"url": "https://github.com/Rich-Harris/degit/issues"
	},
	"homepage": "https://github.com/Rich-Harris/degit#readme",
	"devDependencies": {
		"@rollup/plugin-commonjs": "18.0.0",
		"@rollup/plugin-node-resolve": "11.2.1",
		"chalk": "4.1.0",
		"enquirer": "2.3.6",
		"eslint": "7.23.0",
		"eslint-config-prettier": "8.1.0",
		"eslint-plugin-import": "2.22.1",
		"fuzzysearch": "1.0.3",
		"home-or-tmp": "3.0.0",
		"https-proxy-agent": "5.0.0",
		"husky": "6.0.0",
		"lint-staged": "10.5.4",
		"mocha": "8.3.2",
		"mri": "1.1.6",
		"prettier": "2.2.1",
		"rimraf": "3.0.2",
		"rollup": "2.44.0",
		"rollup-plugin-commonjs": "10.1.0",
		"sander": "0.6.0",
		"source-map-support": "0.5.19",
		"tar": "6.1.0",
		"tiny-glob": "0.2.8"
	},
	"files": [
		"help.md",
		"dist"
	],
	"husky": {
		"hooks": {
			"pre-commit": "lint-staged"
		}
	},
	"lint-staged": {
		"*.{js}": [
			"eslint --fix",
			"git add"
		],
		"*.{js, json, yml, md}": [
			"prettier --write",
			"git add"
		]
	}
}

import resolve from '@rollup/plugin-node-resolve';
import commonjs from '@rollup/plugin-commonjs';
import { builtinModules } from 'module';
import pkg from './package.json';

export default {
	input: {
		index: 'src/index.js',
		bin: 'src/bin.js'
	},
	output: {
		dir: 'dist',
		entryFileNames: '[name].js',
		chunkFileNames: '[name]-[hash].js',
		format: 'cjs',
		exports: 'auto',
		sourcemap: true
	},
	external: Object.keys(pkg.dependencies || {}).concat(builtinModules),
	plugins: [resolve(), commonjs()]
};
