    "@sindresorhus/slugify": "^2.1.0",
    "@tunnckocore/prettier-config": "1.3.8",
    "del-cli": "3.0.0",
    "eslint": "6.8.0",
    "eslint-config-airbnb-base": "14.1.0",
    "eslint-config-prettier": "6.11.0",
    "eslint-plugin-import": "2.20.2",
    "eslint-plugin-prettier": "3.1.3",
    "express": "^4.21.1",
    "formdata-polyfill": "^4.0.10",
    "husky": "4.2.5",
    "jest": "27.2.4",
    "koa": "2.11.0",
    "lint-staged": "10.2.7",
    "make-dir-cli": "2.0.0",
