  },
  "commitlint": {
    "extends": [
      "@commitlint/config-conventional"
    ]
  },
  "lint-staged": {
    "!*.{js,jsx,ts,tsx}": [
      "yarn run fmt:prepare"
    ],
    "*.{js,jsx,ts,tsx}": [
      "yarn run lint"
    ]
  },
  "renovate": {
    "extends": [
      "@tunnckocore",
      ":pinAllExceptPeerDependencies"
    ]
  },
  "packageManager": "yarn@1.22.17",
  "keywords": [
    "multipart",
    "form",
    "data",
    "querystring",
    "www",
    "json",
    "ulpoad",
    "file"
  ]
