import babel from 'rollup-plugin-babel';
import { eslint } from 'rollup-plugin-eslint';
import json from 'rollup-plugin-json';
import { terser } from 'rollup-plugin-terser';
import { version } from './package.json';

const globals = {
  'backbone': 'Backbone',
  'underscore': '_',
  'backbone.radio': 'Backbone.Radio'
};

const now = new Date();
const year = now.getFullYear();

const banner = `/**
* @license
* MarionetteJS (Backbone.Marionette)
* ----------------------------------
* v${version}
*
* Copyright (c)${year} Derick Bailey, Muted Solutions, LLC.
* Distributed under MIT license
*
* http://marionettejs.com
*/\n\n`;

const footer = 'this && this.Marionette && (this.Mn = this.Marionette);';

export default [
  {
    input: 'src/backbone.marionette.js',
    external: ['underscore', 'backbone', 'backbone.radio'],
    output: [
      {
        file: 'lib/backbone.marionette.js',
        format: 'umd',
        name: 'Marionette',
        exports: 'named',
        sourcemap: true,
        globals,
        banner,
        footer
      },
      {
        file: 'lib/backbone.marionette.esm.js',
        format: 'es'
      }
    ],
    plugins: [
      eslint({ exclude: ['package.json'] }),
      json(),
      babel()
    ]
  },
  {
    input: 'src/backbone.marionette.js',
    external: ['underscore', 'backbone', 'backbone.radio'],
    output: [
      {
        file: 'lib/backbone.marionette.min.js',
        format: 'umd',
        name: 'Marionette',
        exports: 'named',
        sourcemap: true,
        globals,
        banner,
        footer
      }
    ],
    plugins: [
      json(),
      babel(),
      terser({ output: { comments: /@license/ }})
    ]
  }
]

var Travis = require('travis-ci');
var repo = 'marionettejs/marionettejs.com';
var travis = new Travis({
  version: '2.0.0',
  headers: {
    'User-Agent': 'Travis/1.0'
  }
});

var getLastMainBuildId = function(builds) {
  var id;

  for (var i = 0; i < builds.length; i++) {
    if (!builds[i].pull_request) {
      id = builds[i].id;
      break;
    }
  }

  if (!id) {
    throw new Error('Build id was not found');
  }

  return id;
};

travis.authenticate({
  github_token: process.env.GH_TOKEN
}, function (err, res) {
  if (err) {
    return console.error(err);
  }

  //get repo builds
  travis.repos(repo.split('/')[0], repo.split('/')[1]).builds.get(function (err, res) {
    if (err) {
      return console.error(err);
    }

    //rebuild latest build
    travis.requests.post({
      build_id: getLastMainBuildId(res.builds)
    }, function (err, res) {
      if (err) {
        return console.error(err);
      }
      console.log(res.flash[0].notice);
    });
  });
});
