{
    "$schema": "https://biomejs.dev/schemas/1.9.4/schema.json",
    "vcs": {
        "enabled": true,
        "clientKind": "git",
        "useIgnoreFile": true,
        "defaultBranch": "dev"
    },
    "files": {
        "ignoreUnknown": false,
        "ignore": [
            "__html__/**/*",
            "__tests__/**/*",
            "lib/**/*",
            "deploy.js",
            "deploy-dev.js",
            "watch.js",
            "build.js",
            "generate-tests.js",
            "biome.json",
            "package-lock.json",
            "node_modules/**/*"
        ]
    },
    "formatter": {
        "enabled": true,
        "indentStyle": "space",
        "lineWidth": 120,
        "indentWidth": 4,
        "lineEnding": "lf"
    },
    "organizeImports": {
        "enabled": true
    },
    "linter": {
        "enabled": true,
        "rules": {
            "recommended": true
        }
    },
    "javascript": {
        "formatter": {
            "quoteStyle": "double"
        }
    }
}

import { build } from 'esbuild';
import { lessLoader } from "esbuild-plugin-less";
import progress from "@olton/esbuild-plugin-progress";
import autoprefixer from "@olton/esbuild-plugin-autoprefixer";
import unlink from "@olton/esbuild-plugin-unlink";
import { replace } from "esbuild-plugin-replace";
import pkg from "./package.json" with {type: "json"};

const production = process.env.MODE === "production"
const version = pkg.version

const banner = `
/*!
 ███╗   ███╗███████╗████████╗██████╗  ██████╗     ██╗   ██╗██╗
 ████╗ ████║██╔════╝╚══██╔══╝██╔══██╗██╔═══██╗    ██║   ██║██║
 ██╔████╔██║█████╗     ██║   ██████╔╝██║   ██║    ██║   ██║██║
 ██║╚██╔╝██║██╔══╝     ██║   ██╔══██╗██║   ██║    ██║   ██║██║
 ██║ ╚═╝ ██║███████╗   ██║   ██║  ██║╚██████╔╝    ╚██████╔╝██║
 ╚═╝     ╚═╝╚══════╝   ╚═╝   ╚═╝  ╚═╝ ╚═════╝      ╚═════╝ ╚═╝                                                             

 * Metro UI v${version} Components Library  (https://metroui.org.ua)
 * Copyright 2012-${new Date().getFullYear()} by Serhii Pimenov
 * Licensed under MIT
 */
`

await build({
    entryPoints: ['./source/default.js'],
    outfile: './lib/metro.js',
    bundle: true,
    minify: production,
    sourcemap: false,
    banner: {
        js: banner
    },
    plugins: [
        progress({
            text: 'Building Metro UI...',
            succeedText: `Metro UI built successfully in %s ms!`
        }),
        lessLoader(),
        autoprefixer(),
        replace({
            '__BUILD_TIME__': new Date().toLocaleString(),
            '__VERSION__': version,
        })
    ],
})

await build({
    entryPoints: ['./source/index.js'],
    outfile: './lib/metro.all.js',
    bundle: true,
    minify: production,
    sourcemap: false,
    banner: {
        js: banner
    },
    plugins: [
        progress({
            text: 'Building Metro UI with icons...',
            succeedText: 'Metro UI with icons built successfully in %s ms!'
        }),
        lessLoader(),
        autoprefixer(),
        replace({
            '__BUILD_TIME__': new Date().toLocaleString(),
            '__VERSION__': version,
        })
    ],
})

await build({
    entryPoints: ['./source/icons.js'],
    outfile: './lib/icons.js',
    bundle: true,
    minify: production,
    sourcemap: false,
    plugins: [
        progress({
            text: 'Building Metro UI icons...',
            succeedText: 'Metro UI icons built successfully in %s ms!'
        }),
        lessLoader(),
        unlink({
            files: ['./lib/icons.js']
        })
    ],
})

import FtpDeploy from "ftp-deploy"
import auth from "./ftpauth.json" with {type: "json"}
import { dirname } from 'path';
import { fileURLToPath } from 'url';

const __dirname = dirname(fileURLToPath(import.meta.url));

const ftpDeploy = new FtpDeploy();

const config = {
    user: auth.keycdn.username,
    password: auth.keycdn.password,
    host: auth.keycdn.host,
    port: auth.keycdn.port,
    localRoot: __dirname + "/lib",
    remoteRoot: `/dev/`,
    include: ["*.*"],
    exclude: [],
    deleteRemote: true,
    forcePasv: true,
    sftp: false,
};

ftpDeploy.on("uploading", function (data) {
    console.log(data.filename); // partial path with filename being uploaded
});

ftpDeploy
    .deploy(config)
    .then((res) => console.log("Deploy finished"))
    .catch((err) => console.log(err));

import FtpDeploy from "ftp-deploy"
import auth from "./ftpauth.json" assert {type: "json"}
import pkg from "./package.json" assert {type: "json"}
import { dirname } from 'path';
import { fileURLToPath } from 'url';

const __dirname = dirname(fileURLToPath(import.meta.url));

const ftp = new FtpDeploy();

const configVer = {
    user: auth.keycdn.username,
    password: auth.keycdn.password,
    host: auth.keycdn.host,
    port: auth.keycdn.port,
    localRoot: __dirname + "/lib",
    remoteRoot: `/${pkg.version}/`,
    include: ["*.*"],
    exclude: [],
    deleteRemote: true,
    forcePasv: true,
    sftp: false,
};

const configCurrent = {
    user: auth.keycdn.username,
    password: auth.keycdn.password,
    host: auth.keycdn.host,
    port: auth.keycdn.port,
    localRoot: __dirname + "/lib",
    remoteRoot: `/current/`,
    include: ["*.*"],
    exclude: [],
    deleteRemote: true,
    forcePasv: true,
    sftp: false,
};

const run = async () => {
    let result

    result = await ftp.deploy(configVer)
    console.log(result)
    result = await ftp.deploy(configCurrent)
    console.log(result)
}

run()

import fs from "fs";
import * as path from "path";

const readFiles = (dirname, onFile, onError = () => {}) => {
    fs.readdir(dirname, function(err, filenames) {
        if (err) {
            onError(err);
            return;
        }
        filenames.forEach(function(filename) {
            fs.readFile(dirname + filename, 'utf-8', function(err, content) {
                if (err) {
                    onError(err);
                    return;
                }
                onFile(filename, content);
            });
        });
    });
}

const sourceDir = './__html__/'
const targetDir = './__tests__/'
const test = `
import {beforeAll, afterAll, describe, it, expect, delay, getFileUrl, B} from "@olton/easytest";

beforeAll(async () => {
    await B.create()
})

afterAll(async () => {
    await B.bye()
})

describe("$file tests", () => {
    it("$file", async () => {
        await B.visit(\`\$\{getFileUrl(\`./__html__/$file\`\)\}\`)
        expect(B.error).toBeNull(B.error)
    })
})
`

fs.readdir(targetDir, (err, files) => {
    if (err) throw err;

    for (const file of files) {
        fs.unlink(path.join(targetDir, file), (err) => {
            if (err) throw err;
        });
    }
});

readFiles(sourceDir, (filename, content) => {
    const [name, _] = filename.split(".")
    fs.writeFile(`${targetDir}/${name}.test.js`, `${test.replaceAll('$file', filename)}`, function (error) {
        if (error) {
            throw error;
        } else {
            console.log(`File ${filename} created`);
        }
    });
}, (e) => {
    console.log(e)
})

