  },
  "homepage": "https://github.com/filamentgroup/loadCSS",
  "engines": {
    "node": ">= 11.9.0"
  },
  "devDependencies": {
    "grunt": "^1.1.0",
    "grunt-cli": "~1.3.2",
    "grunt-contrib-concat": "^1.0.1",
    "grunt-contrib-jshint": "~2.0.0",
    "grunt-contrib-qunit": "^3.1.0",
    "grunt-contrib-uglify": "^4.0.0",
    "husky": "^1.3.1",
    "matchdep": "^2.0.0"
  },
  "scripts": {
    "start": "npx ./server.js",
    "test": "npx grunt"
  },
