var port = 8080;

var filename = "[name]";
var sourceMapFilename = "[name]";
if (LEGACY) {
	filename += ".legacy";
}
if (MINIMIZE) {
	filename += ".min.js";
	sourceMapFilename += ".min.js.map";
} else {
	filename += ".js";
	sourceMapFilename += ".js.map";
}

module.exports = {
	mode: process.env.NODE_ENV,
	entry: {
		"epub": "./src/epub.js",
