"use strict";

module.exports = function(grunt) {

    // Project configuration.
    grunt.initConfig({
        pkg: grunt.file.readJSON("package.json"),
        jshint: {
            files: ["*.js", "public/libs/earth/**/*.js"],
            options: {
                // ignores: [""],
                globals: {
                    Buffer: false,
                    console: false,
                    exports: false,
                    module: false,
                    process: false,
                    require: false,
                    __dirname: false
                },
                globalstrict: true
            }
        }
    });

    // Load the plugin that provides the "jshint" task.
    grunt.loadNpmTasks("grunt-contrib-jshint");

    // Default task(s).
    grunt.registerTask("default", ["jshint"]);

};

/**
 * dev-server - serves static resources for developing "earth" locally
 */

"use strict";

console.log("============================================================");
console.log(new Date().toISOString() + " - Starting");

var util = require("util");

/**
 * Returns true if the response should be compressed.
 */
function compressionFilter(req, res) {
    return (/json|text|javascript|font/).test(res.getHeader('Content-Type'));
}

/**
 * Adds headers to a response to enable caching.
 */
function cacheControl() {
    return function(req, res, next) {
        res.setHeader("Cache-Control", "public, max-age=300");
        return next();
    };
}

function logger() {
    express.logger.token("date", function() {
        return new Date().toISOString();
    });
    express.logger.token("response-all", function(req, res) {
        return (res._header ? res._header : "").trim();
    });
    express.logger.token("request-all", function(req, res) {
        return util.inspect(req.headers);
    });
    return express.logger(
        ':date - info: :remote-addr :req[cf-connecting-ip] :req[cf-ipcountry] :method :url HTTP/:http-version ' +
        '":user-agent" :referrer :req[cf-ray] :req[accept-encoding]\\n:request-all\\n\\n:response-all\\n');
}

var port = process.argv[2];
var express = require("express");
var app = express();

app.use(cacheControl());
app.use(express.compress({filter: compressionFilter}));
app.use(logger());
app.use(express.static("public"));

app.listen(port);
console.log("Listening on port " + port + "...");

/**
 * expand - expands templates with internationalized strings. The resulting HTML files are written to disk.
 *
 * This utility builds the Japanese and English versions of the HTML files. The templates are written using Swig,
 * and roughly follows the methodology described at http://ejohn.org/blog/a-strategy-for-i18n-and-node/
 *
 * English files are placed in the root: public/
 * Japanese files are placed under the language code: public/jp
 * ...and so on for other languages, if ever...
 *
 * don't really care about making the code nice at the moment
 * ideally, this would be a grunt task run at build time
 */

"use strict";

var fs = require("fs");
var path = require("path");
var swig = require("swig");
var mkdirp = require("mkdirp");
var dictionary = require("./public/templates/il8n.json");

var templateDir = "public/templates";

var templates = [
    "index.html",
    "about.html"
];

var languages = [
    {code: "en", target: "public"},
    {code: "ja", target: "public/jp"}  // *lang* code for Japanese is JA not JP. Too late now. Site already public.
];

function newContext(languageCode) {
    return {
        __: function(s) {
            var entry = dictionary[s];
            if (!entry) {
                console.error("unknown il8n key: " + s);
            }
            return entry && entry[languageCode] || s;
        }
    };
}

templates.forEach(function(file) {
    var template = swig.compileFile(path.join(templateDir, file));

    languages.forEach(function(language) {

        var context = newContext(language.code);
        var result = template(context);

        mkdirp.sync(language.target);
        fs.writeFileSync(path.join(language.target, file), result);
    });
});

/*
 * find-chars - small utility to find all unique chars in all text files not already contained in characters.txt.
 *
 * The file characters.txt is used to build a subset font. The find-chars utility allows (somewhat incomplete)
 * detection of new chars that may not be included in the site's font, requiring a font rebuild.
 *
 * Font sub-setting of the M+ font used for the site can be performed here: http://font-face.jp/#mplus-2p-light
 *
 * don't really care about making the code nice at the moment
 * ideally, this would be a grunt task run at build time, and would warn/error on any new chars found
 */

"use strict";

var fs = require("fs");
var path = require("path");
var when = require("when");
var guard = require('when/guard');

/**
 * Recursively walks a directory, invoking onFile for each file found.
 *
 * @param dir the starting directory of the walk
 * @param onFile a callback function(err, file, name, dir, stats) where file is the path of the file relative to
 *               the start of the walk, name is the name of the file, dir is the directory containing the
 *               the file, and stats is the fs.Stats object for the file. If the file is a directory, the callback
 *               can return true to skip walking the contents of the directory.
 */
function walk(dir, onFile) {
    var d = when.defer();
    var pending = 1;

    function visit(dir, name) {
        var file = path.join(dir, name);
        fs.stat(file, function(err, stats) {
            var abort = onFile(err, file, name, dir, stats);
            if (!abort && stats && stats.isDirectory()) {
                return expand(file);
            }
            if (!--pending) {
                d.resolve();
            }
        });
    }

    function expand(dir) {
        fs.readdir(dir, function(err, names) {
            pending += names.length;
            names.forEach(function(name) {
                visit(dir, name);
            });
            if (!--pending) {
                d.resolve();
            }
        });
    }

    expand(dir);
    return d.promise;
}

var inspections = [];
var existingChars = {};
var uniqueChars = {};

function inspect(target, file) {
    var d = when.defer();
    fs.readFile(file, {encoding: "utf8"}, function(err, data) {
        if (err) {
            return console.error(err);
        }
        for (var i = 0; i < data.length; i++) {
            // UNDONE: support for surrogate pairs
            var c = data.charAt(i);
            target[c] = true;
        }
        d.resolve();
    });
    return d.promise;
}

var inspectUnique_throttled = guard(guard.n(5), inspect.bind(null, uniqueChars));

function onFile(err, file, name, dir, stats) {
    if (err) {
        return console.error(err);
    }
    if (name.substr(0, 1) === ".") {
        return true;  // ignore hidden directories and files
    }
    if (name === "node_modules") {
        return true;  // ignore all node module sources
    }
    if (name === "characters.txt") {
        return true;  // ignore file we're diffing against
    }
    if (dir.substring(dir.length - 4) === "data") {
        return true;  // ignore the data directory
    }
    if (stats.isFile() && /\.(js|json|txt|html|css|md)$/.test(name)) {
        inspections.push(when(file).then(inspectUnique_throttled));
    }
}

inspect(existingChars, "characters.txt").then(function() {
    return walk("public", onFile).then(function() {
        return when.all(inspections).then(function() {
            var n = 0, keys = Object.keys(uniqueChars);
            keys.sort();
            for (var i = 0; i < keys.length; i++) {
                if (existingChars[keys[i]]) {
                    continue;
                }
                console.log("'" + keys[i] + "'");
                n++;
            }
            console.log("\nFound " + n + " new chars.");
        });
    });
}).otherwise(console.error);
