const SmartBannerPlugin = require('smart-banner-webpack-plugin');
const path = require('path');
const ExtractTextPlugin = require('extract-text-webpack-plugin');

const src = path.join(__dirname, 'src');
const pkg = require('./package.json');

module.exports = {
  context: src,
  entry: {
    webslides: './js/full.js'
  },
  output: {
    filename: '[name].js',
    path: path.join(__dirname, 'static/js'),
    publicPath: '/static/js/'
  },
  devServer: {
    contentBase: __dirname,
    host: '0.0.0.0'
  },
  module: {
    rules: [
      {
        test: /\.js$/,
        use: [
          'babel-loader',
          'eslint-loader'
        ],
        include: src
      },
      {
        test: /\.scss$/,
        use: ExtractTextPlugin.extract({
          fallback: 'style-loader',
          use: 'css-loader?url=false!postcss-loader!sass-loader'
        }),
        include: src
      }
    ]
  },
  plugins: [
    new ExtractTextPlugin('../css/webslides.css'),
    new SmartBannerPlugin({
      banner: `Name: WebSlides\nVersion: ${pkg.version}\nDate: ${new Date().toISOString().slice(0,10)}\nDescription: ${pkg.description}\nURL: ${pkg.homepage}\nCredits: @jlantunez, @LuisSacristan, @Belelros`,
      raw: false,
      entryOnly: true
    })
  ],
};

/* eslint-disable no-console */
const fs = require('fs');
const archiver = require('archiver');
const pck = require('./package.json');

const output = fs.createWriteStream(`${__dirname}/${pck.name}.zip`);
const archive = archiver('zip', { zlib: { level: 9 } });

output.on('close', () => {
  console.log(`${archive.pointer()} total bytes`);
});

archive.on('error', err => {
  throw err;
});

archive.pipe(output);

pck.release.files.forEach(f => archive.glob(f));

archive.finalize();
