module.exports = {
  presets: [
    ["@babel/preset-env", {
      "targets": {
        "node": "current"
      },
      useBuiltIns: 'usage'
    }]
  ],
  env: {
    test: {
      presets: [
        ['@babel/preset-env', {
          targets: {
            node: 'current'
          },
          modules: 'commonjs',
          useBuiltIns: false,
          debug: false
        }]
      ],
      "plugins": [
        "@babel/plugin-transform-modules-commonjs",
        "istanbul"
      ]
    }
  }
}

{
    "tags": {
        "allowUnknownTags": true,
        "dictionaries": ["jsdoc"]
    },
    "plugins": [
        "node_modules/jsdoc-babel",
        "plugins/markdown",
        "docs/template/custom.js"
    ],
    "templates": {
      "cleverLinks": false,
        "monospaceLinks": true,
        "useLongnameInNav": false,
        "showInheritedInNav": true
    },
    "opts": {
        "destination": "./docs/public/",
        "encoding": "utf8",
        "private": true,
        "recurse": true,
        "template": "./docs/template"
    },
    "babel": {
      "babelrc": false,
      "presets": [
        ["@babel/preset-env", {
          "targets": {
            "browsers": ["last 2 versions", "safari >= 7"]
          },
          "modules": false
        }]
      ],
      "plugins": [
        "@babel/plugin-external-helpers",
        ["@babel/plugin-transform-runtime", {
          "polyfill": false,
          "regenerator": true
        }],
        "@babel/plugin-proposal-class-properties",
        ["@babel/plugin-proposal-decorators", {
          "legacy": true
        }],
        "@babel/plugin-transform-async-to-generator"
      ]
    }
}

