const resolve = require('resolve-dir');
const subdirs = require('subdirs');
const isGit = require('is-git');
const chalk = require('chalk');
const gitlog = require('gitlog').default;
const async = require("async");
const { exec } = require("child_process");

function craftGetGitUserErrorMessage(error, stdout) {
  return `ERROR reading git-config.
    Use e.g. 'git config --global user.name "Mona Lisa"'.
    See 'man git config' for further information.
    \n${error.message}\n${stdout}\n`;
}

async function getGitUser() {
  return new Promise((resolvePromise, rejectPromise) => {
    exec('git config --get user.name', (error, stdout) => {
      if (error || stdout.trim() === '') {
        rejectPromise(craftGetGitUserErrorMessage(error, stdout));
        return;
      }
      resolvePromise(stdout.trim());
    });
  });
}

/**
 * Go through all `repos` and look for subdirectories up to a given `depth`
 * and look for repositories.
 * Calls `callback` with array of repositories.
 */
function findGitRepos(repos, depth, callback) {
  let allRepos = [];
  async.each(repos, (repo, repoDone) => {
    repo = resolve(repo);
    subdirs(repo, depth, (err, dirs) => {
      if (err) {
        switch (err.code) {
          case 'ENOENT':
            return callback(`Could not open directory directory: ${err.path}\n`, null);
          case 'EACCES':
            return; //ignore if no access
          default:
            return callback(`Error "${err.code}" doing "${err.syscall}" on directory: ${err.path}\n`, null);
        }
      }
      if (dirs) dirs.push(repo);
      async.each(dirs, (dir, dirDone) => {
        isGit(dir, (err, isGit) => {
          if (err) {
            return callback(err, null);
          }
          if (!dir.includes('.git') && isGit) {
            allRepos.push(dir);
          }
          dirDone();
        });
      }, repoDone);
    });
  }, err => {
    callback(err, allRepos.sort().reverse());
  });
}

/**
 * returns all commits of the last given `days`.
 * Calls `callback` with line-seperated-strings of the formatted commits.
 */
async function getCommitsFromRepos(repos, days, callback) {
  let cmts = [];
  let errs = [];
  let gitUser;
  try {
    gitUser = await getGitUser();
  } catch( error ) {
    callback(error);
    return;
  }
  async.each(repos, (repo, repoDone) => {
    try {
      gitlog({
        repo: repo,
        all: true,
        number: 100, //max commit count
        since: `${days} days ago`,
        fields: ['abbrevHash', 'subject', 'authorDateRel', 'authorName'],
        author: gitUser
      }, (err, logs) => {
        // Error
        if (err) {
          errs.push(`${chalk.red('Oh noes üò± :')} Getting commits from repo "${repo}" has failed: ${err.message || err}`);
          return repoDone();
        }

        // Find user commits
        let commits = [];
        logs.forEach(c => {
          // filter simple merge commits
          if (c.status && c.status.length)
            commits.push(`${c.abbrevHash} - ${c.subject} (${c.authorDateRel}) <${c.authorName.replace('@end@\n','')}>`);
        });

        // Add repo name and commits
        if (commits.length >= 1) {
          // Repo name
          cmts.push(repo);
          cmts.push(...commits);
        }

        repoDone();
      });
    } catch(err) {
      callback(err, null);
    }
  }, err => {
    const errors = errs.length > 0 ? errs.join('\n\n') : null;
    const commits = cmts.length > 0 ? cmts.join('\n') : "Nothing yet. Start small!";
    callback(err, errors ? [errors, commits].join('\n') : commits);
  });
}

module.exports.findGitRepos = findGitRepos;
module.exports.getCommitsFromRepos = getCommitsFromRepos;

var States = {
  RUNNING: 'running',
  STOPPED: 'stopped',
  IN_BREAK: 'in_break',
  PAUSED: 'paused',
};


// options (object)
// options.onTick (function) -  Runs on every tick
// options.onBreakStarts (function) - Runs when break starts
// options.onBreakEnds (function) - Runs when break ends
var pomodoro = function(options) {
  var _setIntervalId = null;
  var _runningDuration = options.runningDuration; // Default pomodoro duration: 20 Min
  var _breakDuration = options.breakDuration;    // Default break duration: 5 Min
  var _runningDurationRemaining = 0; // In seconds
  var _breakDurationRemaining = 0;   // In seconds
  var _currentState = States.STOPPED;

  var _onTick = function() {
    switch (_currentState) {
      case States.RUNNING: _handleTickOnRunning(); break;
      case States.IN_BREAK: _handleTickOnBreak(); break;
      case States.STOPPED: _handleTickOnStopped(); break;
      case States.PAUSED: return;
    }
  }

  var _handleTickOnRunning = function() {
    if (_runningDurationRemaining < 1) {
      _runningDurationRemaining = 0;
      _breakDurationRemaining = _breakDuration * 60;
      _currentState = States.IN_BREAK
      options.onBreakStarts && options.onBreakStarts();
    } else {
      _runningDurationRemaining -= 1;
      if (options.onTick) options.onTick()
    }
  };

  var _handleTickOnBreak = function() {
    if (_breakDurationRemaining < 1) {
      _breakDurationRemaining = 0;
      _runningDurationRemaining = _runningDuration * 60;
      _currentState = States.RUNNING
      options.onBreakEnds && options.onBreakEnds();
    } else {
      _breakDurationRemaining -= 1;
      if (options.onTick) options.onTick()
    }
  };

  var _handleTickOnStopped = function() {
    clearInterval(_setIntervalId)
    _runningDurationRemaining = 0;
    _breakDurationRemaining = 0;
    _setIntervalId = null;
  };

  var exports = {
    start: function() {
      if (_setIntervalId !== null) clearInterval(_setIntervalId);
      _runningDurationRemaining = _runningDuration * 60;
      _setIntervalId = setInterval(_onTick, 1000);
      _currentState = States.RUNNING;
    },

    stop: function() {
      _currentState = States.STOPPED;
    },

    pause: function() {
      _currentState = States.PAUSED;
    },

    resume: function() {
      _currentState = _breakDurationRemaining ? States.IN_BREAK : States.RUNNING;
    },

    updateRunningDuration() {
      if (_runningDuration >= 60) _runningDuration = 1;
      else _runningDuration += 1;
    },

    updateBreakDuration() {
      if (_breakDuration >= 60) _breakDuration = 1;
      else _breakDuration += 1;
    },

    getRunningDuration() {
      return _runningDuration;
    },

    getBreakDuration() {
      return _breakDuration;
    },

    getRemainingTime() {
      var remainingTime;
      switch (_currentState) {
        case States.RUNNING: remainingTime =  _runningDurationRemaining; break;
        case States.IN_BREAK: remainingTime = _breakDurationRemaining; break;
        case States.STOPPED: remainingTime = _runningDuration * 60; break;
        case States.PAUSED: remainingTime = _runningDurationRemaining || _breakDurationRemaining;
      }
      return ('0' + Math.floor(remainingTime/60)).slice(-2) + ':' + ('0' + remainingTime % 60).slice(-2);
    },

    isRunning() {
      return _currentState === States.RUNNING;
    },

    isInBreak() {
      return _currentState === States.IN_BREAK;
    },

    isPaused() {
      return _currentState === States.PAUSED;
    },

    isStopped() {
      return _currentState === States.STOPPED;
    },
  }

  return exports;
}


module.exports = pomodoro;

