#!/bin/sh
# Originally generated by godownloader. It's since been modified.
set -e

usage() {
  this=$1
  cat <<EOF
$this: download go binaries for livebud/bud

Usage: $this [-b] bindir [version]
  -b sets bindir or installation directory, default "/usr/local/bin"
   [version] is a version number from
   https://github.com/livebud/bud/releases
   If version is missing, then an attempt to find the latest will be found.

EOF
  exit 2
}

parse_args() {
  #BINDIR is /usr/local/bin unless set be ENV
  # over-ridden by flag below

  BINDIR=${BINDIR:-/usr/local/bin}
  while getopts "b:h?" arg; do
    case "$arg" in
      b) BINDIR="$OPTARG" ;;
      h | \?) usage "$0" ;;
    esac
  done
  shift $((OPTIND - 1))
  # Only explicitly set version if there's an argument
  if [ -n "$1" ]; then
    VERSION=$1
  fi
}
# this function wraps all the destructive operations
# if a curl|bash cuts off the end of the script due to
# network, either nothing will happen or will syntax error
# out preventing half-done work
execute() {
  tmp_dir=$(mktmpdir)
  echo "$PREFIX: downloading ${TARBALL_URL}"
  # cp -R "release/${NAME}.${FORMAT}" "${tmp_dir}/${TARBALL}"
  http_download "${tmp_dir}/${TARBALL}" "${TARBALL_URL}"

  echo "$PREFIX: verifying checksums"
  # cp -R "release/checksums.txt" "${tmp_dir}/${CHECKSUM}"
  http_download "${tmp_dir}/${CHECKSUM}" "${CHECKSUM_URL}"
  hash_sha256_verify "${tmp_dir}/${TARBALL}" "${tmp_dir}/${CHECKSUM}"

  (cd "${tmp_dir}" && untar "${TARBALL}")

  # we check if the directory we plan to store the binary in is actually writable by
  # the current user. If not , we request for sudo permissions
  # this is done by -w in the if condition
  # -w - write
  # -r - read
  # -x - execution
  # -x is avoided here since the assumption is that the user will install
  # the binary in a directory already in the PATH env var
  if [ -w "$BINDIR" ]; then
    install -d "${BINDIR}"
    install "${tmp_dir}/${NAME}/${BINARY}" "${BINDIR}/"
  else
    echo "Elevated permissions required to install the binary"
    sudo install -d "${BINDIR}"
    sudo install "${tmp_dir}/${NAME}/${BINARY}" "${BINDIR}/"
  fi

  echo "$PREFIX: installed as ${BINDIR}/${BINARY}"
}

is_supported_platform() {
  platform=$1
  found=1
  case "$platform" in
    darwin/amd64) found=0 ;;
    darwin/arm64) found=0 ;;
    darwin/386) found=0 ;;
    linux/amd64) found=0 ;;
    linux/386) found=0 ;;
    windows/amd64) found=0 ;;
    windows/386) found=0 ;;
    freebsd/amd64) found=0 ;;
    freebsd/386) found=0 ;;
    netbsd/amd64) found=0 ;;
    netbsd/386) found=0 ;;
    openbsd/amd64) found=0 ;;
    openbsd/386) found=0 ;;
  esac
  return $found
}
check_platform() {
  if is_supported_platform "$PLATFORM"; then
    # optional logging goes here
    true
  else
    echo "${PREFIX}: platform $PLATFORM is not supported.  Make sure this script is up-to-date and file request at https://github.com/${PREFIX}/issues/new"
    exit 1
  fi
}
adjust_version() {
  echo "${VERSION}"
  if [ -z "${VERSION}" ]; then
    echo "$PREFIX: checking GitHub for latest version"
    VERSION=$(github_last_release "$OWNER/$REPO")
  fi
  # if version starts with 'v', remove it
  VERSION=${VERSION#v}
}
adjust_format() {
  # change format (tar.gz or zip) based on ARCH
  true
}
adjust_os() {
  # adjust archive name based on OS
  true
}
adjust_arch() {
  # adjust archive name based on ARCH
  true
}

