"use strict";

// This file is only used in `./.eslintrc.js` and in the tests – it’s not part
// of the eslint-config-prettier npm package.
//
// NOTE: Keep this file in sync with `./eslint.base.config.js`!

const config = require(".");

const includeDeprecated = !process.env.ESLINT_CONFIG_PRETTIER_NO_DEPRECATED;

module.exports = {
  extends: [
    "google",
    "plugin:flowtype/recommended",
    "plugin:react/all",
    "plugin:unicorn/recommended",
    "plugin:vue/recommended",
  ],
  plugins: [
    "prettier",
    ...new Set(
      Object.keys(config.rules)
        .map((ruleName) => ruleName.split("/"))
        .filter((parts) => parts.length > 1)
        .map((parts) => parts[0])
    ),
  ],
  parserOptions: {
    ecmaVersion: 2018,
    sourceType: "script",
  },
  env: {
    es6: true,
    node: true,
  },
  rules: {
    "indent": "off",
    "linebreak-style": "off",
    "no-dupe-keys": "error",
    "strict": ["error", "global"],
    "prefer-spread": "off",
    "require-jsdoc": "off",
    "prettier/prettier": "error",
    // Force a conflict with eslint-plugin-prettier in test-lint/prettier.js.
    "prefer-arrow-callback": "error",
    // Force a conflict with Prettier in test-lint/flowtype.js.
    "flowtype/object-type-delimiter": ["error", "semicolon"],
    "react/jsx-filename-extension": "off",
    "react/jsx-no-bind": "off",
    // Force a conflict with Prettier in test-lint/standard.js.
    "standard/computed-property-even-spacing": ["error", "even"],
    "unicorn/consistent-function-scoping": "off",
    "unicorn/filename-case": "off",
    "unicorn/no-array-for-each": "off",
    "unicorn/no-array-reduce": "off",
    "unicorn/no-empty-file": "off",
    "unicorn/no-nested-ternary": "off",
    "unicorn/no-null": "off",
    "unicorn/no-reduce": "off",
    "unicorn/numeric-separators-style": "off",
    "unicorn/prefer-flat-map": "off",
    "unicorn/prefer-module": "off",
    "unicorn/prefer-node-protocol": "off",
    "unicorn/prefer-optional-catch-binding": "off",
    "unicorn/prefer-spread": "off",
    "unicorn/prefer-top-level-await": "off",
    "unicorn/prevent-abbreviations": "off",
    // Force a conflict with Prettier in test-lint/@babel.js. and test-lint/babel.js.
    "object-curly-spacing": "off",
    "babel/object-curly-spacing": ["error", "never"],
    "@babel/object-curly-spacing": ["error", "never"],

    // Workaround: These rules are deprecated, but added by eslint-config-google.
    // We have to exclude them when testing the flat config, but also turn them
    // off for the linting tests to pass. It’s time to get rid of eslint-config-google
    // (their GitHub repo is archived as well).
    ...(includeDeprecated
      ? {}
      : {
          "comma-dangle": "off",
          "max-len": "off",
          "operator-linebreak": "off",
          "quotes": "off",
        }),
  },
  overrides: [
    {
      files: ["**/*.{ts,tsx}"],
      parserOptions: { parser: "@typescript-eslint/parser" },
      rules: {
        // Force a conflict with Prettier in test-lint/typescript.js.
        // This is included in "plugin:@typescript-eslint/recommended".
        "@typescript-eslint/indent": "error",
      },
    },
    {
      files: ["test-lint/{react,flowtype}.js"],
      parserOptions: { parser: "@babel/eslint-parser" },
    },
  ],
  settings: {
    react: {
      version: "16",
    },
  },
};

"use strict";

// This is the internal ESLint config for this project itself – it’s not part of
// the eslint-config-prettier npm package. The idea here is to extend some
// sharable config from npm and then include the configs exposed by this package
// as an “eat your own dogfood” test. That feels like a good test, but
// complicates things a little sometimes.
//
// NOTE: Keep this file in sync with `./eslint.config.js`!

module.exports = {
  extends: ["./.eslintrc.base.js", "./index.js", "./prettier.js"],
  rules: {
    "prettier/prettier": "off",
  },
  overrides: [
    {
      files: ["{bin,test,scripts}/**/*.js"],
      rules: {
        "no-undef": "error",
        "no-restricted-syntax": [
          "error",
          {
            selector: "SequenceExpression",
            message:
              "The comma operator is confusing and a common mistake. Don’t use it!",
          },
        ],
        "quotes": [
          "error",
          "double",
          { avoidEscape: true, allowTemplateLiterals: false },
        ],
      },
    },
    {
      files: ["**/*.test.js"],
      env: { jest: true },
    },
  ],
};

"use strict";

// This file is only used in `./eslint.config.js` and in the tests – it’s not part
// of the eslint-config-prettier npm package.
//
// NOTE: Keep this file in sync with `./.eslintrc.base.js`!

const fs = require("fs");
const path = require("path");
const babelOld = require("eslint-plugin-babel");
const babelNew = require("@babel/eslint-plugin");
const flowtype = require("eslint-plugin-flowtype");
const globals = require("globals");
const google = require("eslint-config-google");
const prettier = require("eslint-plugin-prettier");
const react = require("eslint-plugin-react");
const standard = require("eslint-plugin-standard");
const typescriptEslint = require("@typescript-eslint/eslint-plugin");
const unicorn = require("eslint-plugin-unicorn");
const vue = require("eslint-plugin-vue");
const vueParser = require("vue-eslint-parser");
const eslintrcBase = require("./.eslintrc.base");

module.exports = [
  {
    ignores: fs
      .readFileSync(path.join(__dirname, ".eslintignore"), "utf8")
      .trim()
      .split("\n"),
  },
  {
    // TODO: Figure out how to get flowtype running in flat config.
    ignores: ["test-lint/flowtype.js"],
  },
  google,
  {
    plugins: {
      "@typescript-eslint": typescriptEslint,
      "babel": babelOld,
      "@babel": babelNew,
      flowtype,
      prettier,
      react,
      standard,
      unicorn,
      vue,
    },
  },
  {
    rules: flowtype.configs.recommended.rules,
    settings: flowtype.configs.recommended.settings,
  },
  {
    rules: react.configs.all.rules,
  },
  {
    rules: unicorn.configs.recommended.rules,
  },
  {
    languageOptions: {
      ecmaVersion: eslintrcBase.parserOptions.ecmaVersion,
      sourceType: eslintrcBase.parserOptions.sourceType,
      globals: {
        ...globals.es6,
        ...globals.node,
      },
    },
    rules: eslintrcBase.rules,
    settings: eslintrcBase.settings,
  },
  {
    files: ["**/*.vue"],
    processor: vue.processors[".vue"],
    languageOptions: {
      parser: vueParser,
    },
    rules: {
      ...vue.configs.base.rules,
      ...vue.configs.essential.rules,
      ...vue.configs["strongly-recommended"].rules,
      ...vue.configs.recommended.rules,
    },
  },
  ...eslintrcBase.overrides.map(({ parserOptions, ...override }) => ({
    ...override,
    languageOptions: { parser: require(parserOptions.parser) },
  })),
];

"use strict";

// This is the internal ESLint config for this project itself – it’s not part of
// the eslint-config-prettier npm package. The idea here is to extend some
// sharable config from npm and then include the configs exposed by this package
// as an “eat your own dogfood” test. That feels like a good test, but
// complicates things a little sometimes.
//
// NOTE: Keep this file in sync with `./.eslintrc.js`!

const globals = require("globals");
const base = require("./eslint.base.config");
const index = require(".");
const prettier = require("./prettier");
const eslintrc = require("./.eslintrc");

module.exports = [
  ...base,
  index,
  prettier,
  {
    rules: eslintrc.rules,
  },
  ...eslintrc.overrides.map(({ env = {}, ...override }) => ({
    ...override,
    languageOptions: {
      globals: Object.entries(env).reduce(
        (acc, [key, enabled]) => (enabled ? { ...acc, ...globals[key] } : acc),
        {}
      ),
    },
  })),
];

