{
  "name": "undiscord",
  "nameFull": "Undiscord",
  "version": "5.2.3",
  "description": "Delete all messages in a Discord channel or DM (Bulk deletion)",
  "userScript": {
    "namespace": "https://github.com/victornpb/deleteDiscordMessages",
    "match": [
      "https://*.discord.com/app",
      "https://*.discord.com/channels/*",
      "https://*.discord.com/login"
    ],
    "icon": "https://victornpb.github.io/undiscord/images/icon128.png",
    "downloadURL": "https://raw.githubusercontent.com/victornpb/deleteDiscordMessages/master/deleteDiscordMessages.user.js",
    "contributionURL": "https://www.buymeacoffee.com/vitim",
    "grant": "none"
  },
  "license": "MIT",
  "engines": {
    "node": ">=18"
  },
  "repository": {
    "type": "git",
    "url": "git+https://github.com/victornpb/undiscord.git"
  },
  "bugs": {
    "url": "https://github.com/victornpb/undiscord/discussions"
  },
  "homepage": "https://github.com/victornpb/undiscord",
  "author": "victornpb",
  "main": "deleteDiscordMessages.user.js",
  "scripts": {
    "start": "npm run watch",
    "build": "rollup -c",
    "watch": "rollup -c -w",
    "prebuild": "rimraf dist",
    "test": "npm run lint && npm run build",
    "lint": "eslint src",
    "lint:fix": "eslint src --fix",
    "version": "npm run build"
  },
  "files": [
    "dist"
  ],
  "keywords": [
    "delete",
    "messages",
    "discord",
    "wipe",
    "purge",
    "clean",
    "bulk"
  ],
  "devDependencies": {
    "@rollup/plugin-json": "^6.0.0",
    "eslint": "^8.36.0",
    "husky": "^8.0.3",
    "lint-staged": "^13.2.0",
    "rimraf": "^4.4.1",
    "rollup": "^3.20.2",
    "rollup-plugin-baked-env": "^1.0.1",
    "rollup-plugin-banner2": "^1.2.2",
    "rollup-plugin-commonjs": "^10.1.0",
    "rollup-plugin-node-resolve": "^5.2.0",
    "rollup-plugin-serve": "^2.0.2",
    "tiny-dedent": "^1.0.2"
  },
  "husky": {
    "hooks": {
      "pre-commit": "lint-staged"
    }
  },
  "lint-staged": {
    "src/**.js": "npm run lint"
  }
}

import fs from 'fs';
import resolve from 'rollup-plugin-node-resolve';
import commonjs from 'rollup-plugin-commonjs';
import banner from 'rollup-plugin-banner2';
import json from '@rollup/plugin-json';
import serve from 'rollup-plugin-serve';
import bakedEnv from 'rollup-plugin-baked-env';
import S from 'tiny-dedent';

import { string } from './build/strings-plugin.mjs';
import userScriptMetadataBlock from './build/metadata.mjs';
const loadJSON = (path) => JSON.parse(fs.readFileSync(new URL(path, import.meta.url)));
let packageJson = loadJSON('./package.json');

process.env.VERSION = packageJson.version;
const production = !process.env.ROLLUP_WATCH;
const sourcemap = production ? false : 'inline';

const entry = 'src/index.js';

// const assumptions = {
//   constantSuper: true,
//   enumerableModuleMeta: true,
