{
  "presets": [
    ["@babel/preset-env",
      {
        "targets": {
          "browsers": [
            "> 1%",
            "last 2 versions",
            "not ie <= 8"
          ]
        }
      }
    ]
  ],
  "plugins": [
    "@babel/plugin-proposal-class-properties",
    ["transform-gl-matrix", {
      "glMatrixArray": false
    }],
    [
      "@babel/plugin-transform-runtime",
      {
        "corejs": false,
        "helpers": true,
        "regenerator": true,
        "useESModules": false
      }
    ]
  ]
}

export default {
  devServer: {
    port: 9090,
  },
  output: {
    dir: 'docs/demo',
    publicUrl: '.',
    html: {
      title: 'SpriteJS/next - The next generation of spritejs.'
    },
  },
  // themeFile: 'demos/theme.scss',
  staticFolder: 'dist',
  demoList: '.demoList.json',
  name: 'SPRITEJS',
  version: 'next',
  homePage: global.top == null || global.top === global ? 
    'https://github.com/spritejs/spritejs' : location.top,
  logo: '',
  // 可选主题: active4d, allHallowsEve, amy, blackboard, brillianceBlack,
  // brillianceDull, chromeDevtools, cloudsMidnight, clouds, cobalt,
  // dawn, dreamweaver, eiffel, espressoLibre, github, idle, katzenmilch,
  // kuroirTheme, lazy, magicwbAmiga, merbivoreSoft, merbivore, monokai,
  // pastelsOnDark, slushAndPoppies, solarizedDark, solarizedLight,
  // spacecadet, sunburst, textmateMacClassic, tomorrowNightBlue,
  // tomorrowNightBright, tomorrowNightEighties, tomorrowNight, tomorrow,
  // twilight, vibrantInk, zenburnesque, iplastic, idlefingers, krtheme,
  // monoindustrial,
  boxTheme: 'monokai',
  globalPackages: {
    js: ['./spritejs.js'],
    css: [],
  },
  // tab waterfall
  editorViewMode: 'tab',
  editorOptions: {
    disableLayerHinting: true,
  }
};

module.exports = {
  env: {
    jest: true
  },
  globals: {
    spritejs: true,
    globalThis: true,
    Canvas: true,
    Container: true,
    importScripts: true,
    __DEV__: true,
  },
  extends:  "eslint-config-sprite",
  plugins: ['html'],
  rules: {
    "complexity": ["warn", 25],
    'import/prefer-default-export': 'off',
    "no-unused-vars": 'warn',
    'no-restricted-globals': 'off',
  },
}

const webpack = require('webpack');
const EsmWebpackPlugin = require('@purtuga/esm-webpack-plugin');
const path = require('path');
const fs = require('fs');

module.exports = function (env = {}) {
  let babelConf;

  const babelRC = env.esnext ? './.es6.babelrc' : './.babelrc';
  if(fs.existsSync(babelRC)) {
    babelConf = JSON.parse(fs.readFileSync(babelRC));
    babelConf.babelrc = false;
  }

  const plugins = [];

  if(env.mode === 'development') {
    plugins.push(new webpack.HotModuleReplacementPlugin({
      multiStep: true,
    }));
  }

  if(env.module) {
    plugins.push(new EsmWebpackPlugin());
  }

  plugins.push(new webpack.DefinePlugin({
    __DEV__: env.mode === 'development',
  }));

  let filename = '[name]';
  if(env.esnext) filename += '.es';
  if(env.module) filename += 'm';

  if(env.mode === 'production') filename += '.min';
  filename += '.js';

  const entry = {
    spritejs: './src/index',
    'spritejs.worker': './src/index.worker',
  };

  plugins.push(new webpack.DefinePlugin({
    __SPRITEVER__: `"${require('./package.json').version}"`,
  }));

  return {
    mode: env.mode || 'none',
    entry,
    output: {
      path: path.resolve(__dirname, 'dist'),
      filename,
      publicPath: '/js/',
      library: ['spritejs'],
      libraryTarget: env.module ? 'var' : 'umd',
      // libraryExport: 'default',
      globalObject: 'this',
    },
    resolve: {
      alias: {
        'gl-renderer': 'gl-renderer/src',
        '@mesh.js/core': '@mesh.js/core/src',
      },
    },
    module: {
      rules: [
        {
          test: /\.js$/,
          exclude: /node_modules\/(?!@mesh.js|gl-renderer).*/,
          use: {
            loader: 'babel-loader',
            options: babelConf,
          },
        },
        {
          test: /\.(frag|vert|glsl)$/,
          use: {
            loader: 'raw-loader',
            options: {},
          },
        },
      ],

      /* Advanced module configuration (click to show) */
    },

    externals: {

    },
    // Don't follow/bundle these modules, but request them at runtime from the environment

    stats: 'errors-only',
    // lets you precisely control what bundle information gets displayed

    devServer: {
      contentBase: path.join(__dirname, env.server || '.'),
      compress: true,
      port: 9090,
      hot: true,
      // ...
    },

    plugins,
    // list of additional plugins

    /* Advanced configuration (click to show) */
  };
};
