module.exports = {
  input: ['server'],
  ignore: ['server/node_modules', 'server/public'],
  output: 'server-esm',
  forceDirectory: null,
  modules: [],
  extension: {
    use: 'js',
    ignore: [],
  },
  addModuleEntry: false,
  addPackageJson: true,
  filesWithShebang: [],
  codemod: {
    path: '',
    files: ['cjs', 'exports', 'named-export-generation'],
  },
};

{
  "name": "sqlpad-project",
  "version": "7.5.2",
  "private": true,
  "devDependencies": {
    "husky": "^9.0.6",
    "lint-staged": "^15.2.0",
    "prettier": "^2.6.2"
  },
  "prettier": {
    "singleQuote": true
  },
  "lint-staged": {
    "*.{js,json,css}": [
      "prettier --write"
    ]
  },
  "scripts": {
    "fixlint": "npm run fixlint --prefix client && npm run fixlint --prefix server && prettier --write \"**/*.js\"",
    "lint": "npm run lint --prefix client && npm run lint --prefix server && prettier --check \"**/*.js\"",
    "prepare": "husky",
    "precommit": "lint-staged"
  }
}
