{
  "root": true,
  "parserOptions": {
    "ecmaVersion": 2017
  },
  "env": {
    "browser": true,
    "es6": true,
    "webextensions": true
  },
  "extends": [
    "eslint:recommended",
    "prettier"
  ],
  "rules": {
    "no-console": 0,
    "no-unused-vars": [
          "error",
      { "vars": "all", "args": "none", "ignoreRestSiblings": false }
    ],
    "no-undef": ["error"],
    "no-proto": ["error"],
    //        "prefer-arrow-callback": ["warn"],  TODO: refactor to use arrow functions
    //        "no-var": ["error"],  TODO: refactor to use let and const
    "prefer-spread": ["warn"],
//    "semi": ["error", "always"],
    "padded-blocks": ["off", { "blocks": "never" }],
//    "indent": ["error", 2],
    "one-var": ["off", "never"],
    "spaced-comment": ["off", "always"]
//    "space-before-function-paren": [
//      "error",
//      {
//        "anonymous": "always",
//        "named": "never",
//        "asyncArrow": "always"
//      }
//    ]
  }
}

module.exports = function(grunt) {
  require('time-grunt')(grunt);

  grunt.initConfig({
    pkg: grunt.file.readJSON('package.json'),
    manifest: grunt.file.readJSON('src/manifest.json'),
    config: {
      tempDir:
        grunt.cli.tasks[0] === 'tgut' ? 'build/tgut-temp/' : 'build/tgs-temp/',
      buildName:
        grunt.cli.tasks[0] === 'tgut'
          ? 'tgut-<%= manifest.version %>'
          : 'tgs-<%= manifest.version %>',
    },
    copy: {
      main: {
        expand: true,
        src: ['src/**', '!src/tests.html', '!src/js/tests/**'],
        dest: '<%= config.tempDir %>',
      },
    },
    'string-replace': {
      debugoff: {
        files: {
          '<%= config.tempDir %>src/js/':
            '<%= config.tempDir %>src/js/gsUtils.js',
        },
        options: {
          replacements: [
            {
              pattern: /debugInfo\s*=\s*true/,
              replacement: 'debugInfo = false',
            },
            {
              pattern: /debugError\s*=\s*true/,
              replacement: 'debugError = false',
            },
          ],
        },
      },
      debugon: {
        files: {
          '<%= config.tempDir %>src/js/':
            '<%= config.tempDir %>src/js/gsUtils.js',
        },
        options: {
          replacements: [
            {
              pattern: /debugInfo\s*=\s*false/,
              replacement: 'debugInfo = true',
            },
            {
              pattern: /debugError\s*=\s*false/,
              replacement: 'debugError = true',
            },
          ],
        },
      },
      localesTgut: {
        files: {
          '<%= config.tempDir %>src/_locales/':
            '<%= config.tempDir %>src/_locales/**',
        },
        options: {
          replacements: [
            {
              pattern: /The Great Suspender/gi,
              replacement: 'The Great Tester',
            },
          ],
        },
      },
    },
    crx: {
      public: {
        src: [
          '<%= config.tempDir %>src/**/*',
          '!**/html2canvas.js',
          '!**/Thumbs.db',
        ],
        dest: 'build/zip/<%= config.buildName %>.zip',
      },
      private: {
        src: [
          '<%= config.tempDir %>src/**/*',
          '!**/html2canvas.js',
          '!**/Thumbs.db',
        ],
        dest: 'build/crx/<%= config.buildName %>.crx',
        options: {
          privateKey: 'key.pem',
        },
      },
    },
    clean: ['<%= config.tempDir %>'],
  });

  grunt.loadNpmTasks('grunt-contrib-copy');
  grunt.loadNpmTasks('grunt-string-replace');
  grunt.loadNpmTasks('grunt-crx');
  grunt.loadNpmTasks('grunt-contrib-clean');
  grunt.registerTask('default', [
    'copy',
    'string-replace:debugoff',
    'crx:public',
    'crx:private',
    'clean',
  ]);
  grunt.registerTask('tgut', [
    'copy',
    'string-replace:debugon',
    'string-replace:localesTgut',
    'crx:public',
    'crx:private',
    'clean',
  ]);
};

