}
.leftbar-menu-item > a {
	display: block;
	height: 100%;
	width: 100%;
	margin: 0;
	padding: 0 20px;
	overflow-wrap: break-word;
	word-wrap: break-word;
	background-color: transparent;
	color: #32325d !important;
	text-decoration: none;
	outline: none;
	cursor: pointer;
	font-size: 14px;
	transition: background 0.2s ease-in-out;
}
