	content: attr(tooltip-move-to-left);
	top: 1px;
}
#float_action_buttons.fabtns-float-left #fabtn_toggle_sides:before {
	content: attr(tooltip-move-to-right);
}
#float_action_buttons #fabtn_back_to_top:before {
