	position: relative;
}
.comment-image-preview-mask {
	display: none;
}
.comment-item-text .comment-image.comment-image-preview-zoomed .comment-image-preview-mask {
	display: block;
	background: transparent;
	position: fixed;
	left: 0;
	right: 0;
	top: 0;
	bottom: 0;
	width: 100vw;
	height: 100vh;
	z-index: 1501;
	cursor: zoom-out;
}
.comment-item-text .comment-image .comment-image-preview {
	width: 22px;
	height: 22px;
	position: absolute;
	left: 0;
	opacity: 0;
