/*Share.js*/
#share_container {
	height: 38px;
	margin-bottom: 25px;
	position: relative;
}
#share,
#share_show {
	width: max-content;
	width: -moz-max-content;
	transition: all 0.3s ease;
}
#share {
	margin-left: auto;
	max-width: 100%;
	transform: translateY(50px);
	opacity: 0;
	pointer-events: none;
}
#share_show {
	position: absolute;
	right: 0;
	top: 0;
	opacity: 1;
}
#share_container.opened #share {
	transform: none;
	opacity: 1;
	pointer-events: unset;
}
#share_container.opened #share_show {
	top: -50px;
	opacity: 0;
	pointer-events: none;
}
#share_container.opened {
	height: max-content;
	height: -moa-max-content;
	margin-bottom: 10px;
	min-height: 38px;
}
#share_container .btn {
	height: 36px;
	padding: 0 15px;
	line-height: 36px;
	margin-left: 5px;
	margin-right: 0;
	margin-bottom: 15px;
	min-width: 50px;
}
#share .icon-wechat {
	position: relative;
}
#share .icon-wechat .wechat-qrcode {
	position: absolute;
	z-index: 2;
	bottom: 50px;
	right: 0px;
	width: max-content;
	width: -moz-max-content;
	background: var(--color-widgets);
	box-shadow: 0 0.125rem 0.25rem rgba(0, 0, 0, 0.075);
	text-align: center;
	padding: 15px 30px;
	transition: visibility 0.25s, opacity 0.25s, transform 0.25s;
	transform: translate(0, -10px) perspective(100px) rotateX(3deg);
	opacity: 0;
	border-radius: 3px;
	pointer-events: none;
}
#share .icon-wechat:hover .wechat-qrcode {
	transform: translate(0, 0);
	opacity: 1;
}
#share .wechat-qrcode:before {
	position: absolute;
	z-index: 3;
	top: calc(100% - 1em - 2px);
	right: 35px;
	display: block;
	width: 16px;
	height: 16px;
	content: "";
	transform: rotate(-45deg) translateY(1rem);
	border-radius: 0.2rem;
	background: var(--color-widgets);
}
#share .qrcode img {
	margin: auto;
}
#share .wechat-qrcode h4 {
	font-size: 18px;
	padding-bottom: 10px;
}
#share .wechat-qrcode .help {
	color: #000;
	padding-top: 20px;
	padding-bottom: 5px;
}
#share > a {
	position: relative;
}
#share > a:before,
#share_show:before {
	pointer-events: none;
	position: absolute;
	top: -50px;
	right: 0px;
	line-height: 25px;
	width: max-content;
	width: -moz-max-content;
	text-align: center;
	font-weight: normal;
	color: #fff;
	background: #32325d;
	padding: 3px 10px;
	font-size: 14px;
	border-radius: 3px;
	z-index: 3;
	transition: all 0.3s ease;
	transform: translateY(5px);
	opacity: 0;
}
#share > a:hover:before,
#share_show:hover:before {
	transform: translateY(0);
	opacity: 0.7;
}
#share_show:before {
	content: attr(tooltip);
	top: -40px;
	height: max-content;
	height: -moz-max-content;
	text-transform: none;
}
#share .icon-douban:before {
	content: attr(tooltip);
}
#share .icon-qq:before {
	content: attr(tooltip);
}
#share .icon-qzone:before {
	content: attr(tooltip);
}
#share .icon-weibo:before {
	content: attr(tooltip);
}
#share .icon-facebook:before {
	content: attr(tooltip);
}
#share .icon-twitter:before {
	content: attr(tooltip);
}
#share .icon-telegram:before {
	content: attr(tooltip);
}
#share .icon-copy-link:before {
	content: attr(tooltip);
}

