#!/usr/bin/env php
<?php
/**
 * Codeception CLI
 */

require __DIR__ . '/app.php';

@echo off

if "%PHP_PEAR_PHP_BIN%" neq "" (
	set PHPBIN=%PHP_PEAR_PHP_BIN%
) else set PHPBIN=php

"%PHPBIN%" "codecept" %*

{
    "ignore": [
        "tests/*",
        "RoboFile.php",
        "shim.php",
        "phpunit5-loggers.php"
    ]
}
