{
    "env": {
        "browser": true,
        "commonjs": true,
        "es6": true,
        "jquery": true
    },
    "rules": {
        "indent": [
            "error",
            4
        ],
        "linebreak-style": [
            "error",
            "unix"
        ],
        "quotes": [
            "error",
            "single"
        ],
        "class-methods-use-this": 0,
        "radix": ["error", "as-needed"]
    },
    "extends": "airbnb-base",
    "globals": {
        "axios": true,
        "Turbolinks": true
    }
}

{
    "name": "orchid/platform",
    "description": "Platform for back-office applications, admin panel or CMS your Laravel app.",
    "keywords": [
        "framework",
        "laravel",
        "orchid",
        "platform",
        "admin",
        "cmf",
        "cms",
        "rad",
        "extranet"
    ],
    "type": "library",
    "license": "MIT",
    "homepage": "https://orchid.software",
    "support": {
        "issues": "https://github.com/orchidsoftware/platform/issues",
        "source": "https://github.com/orchidsoftware/platform",
        "docs": "https://orchid.software/en/docs",
        "email": "bliz48rus@gmail.com"
    },
    "authors": [
        {
            "name": "Alexandr Chernyaev",
            "email": "bliz48rus@gmail.com",
            "homepage": "https://tabuna.github.io",
            "role": "Developer"
        }
    ],
    "funding": [
        {
            "type": "opencollective",
            "url": "https://opencollective.com/orchid"
        }
    ],
    "require": {
        "composer-runtime-api": "^2.2",
        "composer/semver": "^3.0",
        "laravel/framework": "^10.0 || ^11.0",
        "laravel/scout": "^9.0 || ^10.0",
        "orchid/blade-icons": "^4.0",
        "tabuna/breadcrumbs": "^4.0",
        "twbs/bootstrap-icons": "^1.10",
        "watson/active": "^7.0"
    },
    "require-dev": {
        "fakerphp/faker": "^1.9.1",
        "laravel/pint": "^1.14",
        "orchestra/testbench": "^8.0 || ^9.0",
        "orchestra/testbench-core": "^8.0 || ^9.0",
        "orchestra/testbench-dusk": "^8.0 || ^9.0",
        "phpunit/phpunit": "^10.0 || ^11.0"
    },
    "conflict": {
        "laravel/framework": "<10.48.25 || >=11.34.0",
        "orchestra/testbench-core": "<8.23.9",
        "nesbot/carbon": "<2.66.0"
    },
    "autoload": {
        "psr-4": {
            "Orchid\\Access\\": "src/Access/",
            "Orchid\\Alert\\": "src/Alert/",
            "Orchid\\Attachment\\": "src/Attachment/",
            "Orchid\\Filters\\": "src/Filters/",
            "Orchid\\Metrics\\": "src/Metrics/",
            "Orchid\\Platform\\": "src/Platform/",
            "Orchid\\Screen\\": "src/Screen/",
            "Orchid\\Support\\": "src/Support/"
        },
        "files": [
            "src/Support/helpers.php"
        ]
    },
    "autoload-dev": {
        "psr-4": {
            "Orchid\\Platform\\Database\\Factories\\": "database/factories/",
            "Orchid\\Platform\\Database\\Seeders\\": "database/seeders/",
            "Orchid\\Tests\\": "tests/",
            "App\\": "stubs/app/"
        }
    },
    "extra": {
        "laravel": {
            "providers": [
                "Orchid\\Platform\\Providers\\FoundationServiceProvider"
            ],
            "aliases": {
                "Alert": "Orchid\\Support\\Facades\\Alert",
                "Dashboard": "Orchid\\Support\\Facades\\Dashboard"
            }
        }
    },
    "suggest": {
        "orchid/crud": "Automated CRUD based on your files resources.",
        "orchid/fortify": "Orchid template for Laravel Fortify."
    },
    "config": {
        "sort-packages": true
    }
}

{
    "preset": "laravel",
    "rules": {
        "array_syntax": {
            "syntax": "short"
        },
        "ordered_imports": {
            "sort_algorithm": "alpha"
        },
        "no_unused_imports": true,
        "no_whitespace_before_comma_in_array": true,
        "not_operator_with_successor_space": true,
        "trailing_comma_in_multiline": {
            "elements": [
                "arrays"
            ]
        },
        "phpdoc_scalar": true,
        "unary_operator_spaces": true,
        "binary_operator_spaces": {
            "operators": {
                "=>": "align_single_space"
            }
        },
        "blank_line_before_statement": {
            "statements": [
                "continue",
                "declare",
                "return",
                "throw",
                "try"
            ]
        },
        "phpdoc_separation": true,
        "phpdoc_align": true,
        "phpdoc_order": true,
        "phpdoc_single_line_var_spacing": true,
        "phpdoc_var_without_name": true,
        "no_superfluous_phpdoc_tags": false,
        "class_attributes_separation": {
            "elements": {
                "method": "one"
            }
        },
        "method_argument_space": {
            "on_multiline": "ignore"
        },
        "trim_array_spaces": true,
        "single_trait_insert_per_statement": false,
        "new_with_parentheses": false,
        "php_unit_method_casing": {
            "case": "camel_case"
        }
    }
}

const mix = require('laravel-mix');

/*
 |--------------------------------------------------------------------------
 | Mix Asset Management
 |--------------------------------------------------------------------------
 |
 | Mix provides a clean, fluent API for defining some Webpack build steps
 | for your Laravel application. By default, we are compiling the Sass
 | file for the application as well as bundling up all the JS files.
 |
 */

/* Orchid mix config start */

if (!mix.inProduction()) {
    mix
        .webpackConfig({
            devtool: 'source-map',
        })
        .sourceMaps();
} else {
    mix.options({
        clearConsole: true,
        terser: {
            terserOptions: {
                compress: {
                    drop_console: true,
                },
            },
        },
    });
}

mix
    .sass('resources/sass/app.scss', 'css/orchid.css', {
        implementation: require('sass'),
    })
    .options({
        processCssUrls: false,
    })
    .postCss('public/css/orchid.css', 'css/orchid.rtl.css', [
        require('rtlcss'),
    ])
    .js('resources/js/app.js', 'js/orchid.js')
    .extract([
        'stimulus', 'stimulus/webpack-helpers', 'turbo',
        'popper.js', 'bootstrap',
        'dropzone', 'cropperjs', 'tom-select', 'frappe-charts', 'inputmask',
        'simplemde', 'axios', 'leaflet', 'codeflask',
        'flatpickr', 'quill', 'codemirror', 'typo-js', 'sortablejs',
    ])
    .setPublicPath('public')
    .version();

/* Orchid mix config end */
